import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { FaCar, FaSprayCan, FaBroom, FaShieldAlt, FaPhone, FaEnvelope, FaMapMarkerAlt } from 'react-icons/fa';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const AboutContent = () => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <Hero />
      <Services />
      <Features />
      <Contact />
    </div>
  );
};

const Hero = () => (
  <div className="relative h-screen bg-gray-100 overflow-hidden">
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1.5 }}
      className="absolute inset-0"
    >
      <img 
        src="https://th.bing.com/th/id/R.28e7f5298a45e7c0901707e13bff5980?rik=85uBazOv2TnJvg&riu=http%3a%2f%2fpngimg.com%2fuploads%2fmaserati%2fmaserati_PNG102.png&ehk=T106eiDKTt3gAySWjEaRGITDretZ%2blIpFopT5%2bamQn0%3d&risl=&pid=ImgRaw&r=0" 
        alt="Luxury detailed car" 
        className="w-full h-full object-contain"
      />
    </motion.div>
    
    <div className="absolute inset-0 flex flex-col justify-center items-center">
      <motion.h1 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
