import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import AboutPage from './AboutPage';

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 1500);
    return () => clearTimeout(timer);
  }, []);

  const handleAnimationComplete = () => {
    setTimeout(() => {
      setStep(2);
    }, 4000);
  };

  const variants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 1, ease: "easeOut" }
    },
    exit: { 
      opacity: 0, 
      y: -50, 
      transition: { duration: 1, ease: "easeIn" }
    },
  };

  const textVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 0.8, ease: "easeOut" }
    },
  };

  return (
    <div className="relative w-full h-screen bg-stone-900 overflow-hidden font-sans">
      <AnimatePresence mode="wait">
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-stone-900"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-stone-100"
            onAnimationComplete={handleAnimationComplete}
          >
            <motion.div 
              variants={textVariants}
              className="text-sm mb-4 tracking-widest"
            >
              LOCATED IN SURREY, B.C.
            </motion.div>
            <motion.div 
              variants={textVariants}
              className="text-7xl font-bold mb-6 tracking-tight"
              style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
            >
              JR AUTO DETAILING
            </motion.div>
            <motion.div 
              variants={textVariants}
              className="text-xl mb-4 tracking-wide"
            >
              PREMIUM CAR CARE EXPERIENCE
            </motion.div>
            <motion.div 
              variants={textVariants}
              className="text-sm tracking-widest"
            >
              SERVING THE LOWER MAINLAND
            </motion.div>
          </motion.div>
        )}
        {step === 2 && (
          <motion.div
            key="aboutPage"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
            className="absolute inset-0"
          >
            <AboutPage />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
