import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
