const ServiceCategories = () => {
  const [openCategory, setOpenCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [bookingDetails, setBookingDetails] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingStatus, setBookingStatus] = useState(null);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];

  const handleCategoryClick = (category) => {
    setSelectedType(null);
    setSelectedService(null);
    setBookingDetails(null);
    setShowBookingForm(false);
    setOpenCategory(openCategory === category ? null : category);
  };

  const handleTypeClick = (type) => {
    setSelectedService(null);
    setBookingDetails(null);
    setShowBookingForm(false);
    setSelectedType(selectedType === type ? null : type);
  };

  const handleServiceClick = (service) => {
    setBookingDetails(null);
    setShowBookingForm(false);
    setSelectedService(selectedService === service ? null : service);
  };

  const handleBookingClick = (e, service) => {
    e.stopPropagation();
    setBookingDetails(service);
    setShowBookingForm(true);
  };
  
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const { name, email, date, time } = e.target.elements;
    const bookingInfo = {
      to_email: email.value,
      cc: "timothysuldayan1@gmail.com",
      name: name.value,
      email: email.value,
      date: date.value,
      time: time.value,
      service: bookingDetails.title,
      vehicleType: openCategory,
      price: bookingDetails.prices[openCategory],
      features: bookingDetails.features.join(', '),
      description: bookingDetails.description
    };
  
    setBookingStatus('sending');
  
    emailjs.send('service_co2vfjp', 'template_zjqe62g', bookingInfo, 'y2CeXk4N9lvCdfuzF')
      .then((result) => {
        setBookingStatus('success');
        e.target.reset();
        setBookingDetails(null);
        setShowBookingForm(false);
        setTimeout(() => setBookingStatus(null), 3000);
      }, (error) => {
        setBookingStatus('error');
        console.error('Booking failed:', error);
      });
  };
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1, y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const collapseVariants = {
    hidden: { opacity: 0, height: 0 },
    visible: {
      opacity: 1, height: 'auto',
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    },
    exit: {
      opacity: 0, height: 0,
      transition: {
        duration: 0.6,
        ease: "easeIn"
      }
    }
  };

  return (
    <div className="bg-stone-800 min-h-screen flex items-center justify-center p-8 text-stone-200">
      <motion.div
        className="max-w-6xl w-full"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="mb-12"
        >
          <span className="text-9xl font-bold text-stone-700 tracking-tighter">02</span>
        </motion.div>
        <motion.h1
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="text-8xl font-bold mb-16 text-stone-100 tracking-tight"
        >
          Our Services.
        </motion.h1>
        {carTypes.map((type, index) => (
          <motion.div
            key={index}
            className="mb-8 border-b border-stone-600 pb-4"
            variants={itemVariants}
          >
            <h2
              className="text-5xl font-bold cursor-pointer flex justify-between items-center"
              onClick={() => handleCategoryClick(type)}
              style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
            >
              {type.toUpperCase()}
              <motion.span
                initial={false}
                animate={{ rotate: openCategory === type ? 180 : 0 }}
                transition={{ duration: 0.3 }}
              >
                &#x2B9F;
              </motion.span>
            </h2>
            <AnimatePresence>
              {openCategory === type && (
                <motion.div
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                  variants={collapseVariants}
                  className="mt-4"
                >
                  {['Interior', 'Exterior'].map((serviceType) => (
                    <div key={serviceType} className="mb-4 border-t border-stone-700 pt-4">
                      <h3
                        className="text-4xl font-semibold cursor-pointer flex justify-between items-center"
                        onClick={() => handleTypeClick(serviceType)}
                        style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
                      >
                        {serviceType}
                        <motion.span
                          initial={false}
                          animate={{ rotate: selectedType === serviceType ? 180 : 0 }}
                          transition={{ duration: 0.3 }}
                        >
                          &#x2B9F;
                        </motion.span>
                      </h3>
                      <AnimatePresence>
                        {selectedType === serviceType && (
                          <motion.ul
                            initial="hidden"
                            animate="visible"
                            exit="exit"
                            variants={collapseVariants}
                            className="mt-4 space-y-4"
                          >
                            {services.filter(service => service.type === serviceType).map((service, idx) => (
                              <motion.li
                                key={idx}
                                className="py-2 cursor-pointer border-b border-stone-700"
                                onClick={() => handleServiceClick(service)}
                                variants={itemVariants}
                              >
                                <h4 className="text-3xl font-medium flex justify-between items-center"
                                    style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}>
                                  {service.title.split(' ').pop()}
                                  <motion.span
                                    initial={false}
                                    animate={{ rotate: selectedService === service ? 180 : 0 }}
                                    transition={{ duration: 0.3 }}
                                  >
                                    &#x2B9F;
                                  </motion.span>
                                </h4>
                                <AnimatePresence>
                                  {selectedService === service && (
                                    <motion.div
                                      initial="hidden"
                                      animate="visible"
                                      exit="exit"
                                      variants={collapseVariants}
                                      className="mt-4 bg-stone-700 p-6 rounded-lg"
                                    >
                                      <p className="text-lg mb-4">{service.description}</p>
                                      <ul className="list-disc ml-6 mb-6 space-y-2">
                                        {service.features.map((feature, i) => (
                                          <li key={i}>{feature}</li>
                                        ))}
                                      </ul>
                                      <div className="flex flex-col md:flex-row justify-between items-start md:items-center">
                                        <div>
                                          <h5 className="text-2xl font-bold mb-2">Price:</h5>
                                          <p className="text-xl">{type}: {service.prices[type]}</p>
                                          <motion.button
                                            className="mt-4 px-6 py-3 bg-stone-600 text-stone-100 font-bold rounded-lg transition-colors duration-300 hover:bg-stone-500"
                                            onClick={(e) => handleBookingClick(e, service)}
                                            whileHover={{ scale: 1.05 }}
                                            whileTap={{ scale: 0.95 }}
                                          >
                                            Book Now
                                          </motion.button>
                                        </div>
                                        <div className="mt-6 md:mt-0 md:ml-6">
                                          <img src="https://via.placeholder.com/300x200" alt={service.title} className="w-full md:w-64 h-40 object-cover rounded-lg shadow-lg"/>
                                        </div>
                                      </div>
                                    </motion.div>
                                  )}
                                </AnimatePresence>
                              </motion.li>
                            ))}
                          </motion.ul>
                        )}
                      </AnimatePresence>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        ))}
      </motion.div>
      {showBookingForm && bookingDetails && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center"
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className="bg-stone-800 p-8 rounded-lg w-full max-w-md shadow-xl"
          >
            <h3 className="text-3xl font-bold mb-6 text-stone-100">Book {bookingDetails.title}</h3>
            <form onSubmit={handleBookingSubmit} className="space-y-4">
              <div>
                <label className="block text-xl mb-2 text-stone-300" htmlFor="name">Name:</label>
                <input
                  className="w-full p-3 bg-stone-700 text-stone-100 border border-stone-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-stone-500"
                  type="text"
                  id="name"
                  name="name"
                  required
                />
              </div>
              <div>
                <label className="block text-xl mb-2 text-stone-300" htmlFor="email">Email:</label>
                <input
                  className="w-full p-3 bg-stone-700 text-stone-100 border border-stone-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-stone-500"
                  type="email"
                  id="email"
                  name="email"
                  required
                />
              </div>
              <div>
                <label className="block text-xl mb-2 text-stone-300" htmlFor="date">Date:</label>
                <input
                  className="w-full p-3 bg-stone-700 text-stone-100 border border-stone-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-stone-500"
                  type="date"
                  id="date"
                  name="date"
                  required
                />
              </div>
              <div>
                <label className="block text-xl mb-2 text-stone-300" htmlFor="time">Time:</label>
                <input
                  className="w-full p-3 bg-stone-700 text-stone-100 border border-stone-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-stone-500"
                  type="time"
                  id="time"
                  name="time"
                  required
                />
              </div>
              <motion.button
                className="w-full px-6 py-3 bg-stone-600 text-stone-100 font-bold rounded-lg transition-colors duration-300 hover:bg-stone-500"
                type="submit"
                disabled={bookingStatus === 'sending'}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {bookingStatus === 'sending' ? 'Sending...' : 'Submit Booking'}
              </motion.button>
            </form>
            {bookingStatus === 'success' && (
              <p className="mt-4 text-green-400 font-bold">Booking successful!</p>
            )}
            {bookingStatus === 'error' && (
              <p className="mt-4 text-red-400 font-bold">Booking failed. Please try again.</p>
            )}
            <motion.button
              className="mt-6 w-full px-6 py-3 bg-stone-700 text-stone-300 font-bold rounded-lg transition-colors duration-300 hover:bg-stone-600"
              onClick={() => setShowBookingForm(false)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Close
            </motion.button>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default ServiceCategories;
