// src/components/JRAutoDetailing.js
import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 3000); // Transition after 3 seconds
    return () => clearTimeout(timer);
  }, []);

  const handleAnimationComplete = () => {
    setTimeout(() => {
      setStep(2);
    }, 5000); // Stay on the "NEW ORD3R" screen for 5 seconds
  };

  const variants = {
    hidden: { opacity: 0, x: 100 },
    visible: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -100 },
  };

  return (
    <div className="relative w-full h-screen bg-black">
      <AnimatePresence>
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-black"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-white bg-black p-4"
            onAnimationComplete={handleAnimationComplete}
          >
            <div className="text-xs mb-2">A CONCEPT BY BIKEMAILORDER</div>
            <div className="text-7xl font-bold mb-4 tracking-tight">
              <span className="block">NEW</span>
              <span className="block">
                <span className="inline-block transform rotate-90 origin-center mr-2">Ã˜</span>RD3R
              </span>
            </div>
            <div className="text-xs mb-4 text-center">
              <span className="block">MODULAR</span>
              <span className="block">CYCLING SPACE FOR</span>
              <span className="block">CULTURE</span>
            </div>
            <div className="text-xs text-center">
              <span className="block">KARL-MARX-ALLEE</span>
              <span className="block">10243 BERLIN</span>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
