import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { FaCar, FaSprayCan, FaBroom, FaShieldAlt, FaPhone, FaEnvelope, FaMapMarkerAlt } from 'react-icons/fa';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const AboutContent = () => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <Hero />
      <Features />
      <Services />
      <Contact />
    </div>
  );
};

const Hero = () => (
  <div className="relative h-screen bg-gray-100 overflow-hidden">
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1.5 }}
      className="absolute inset-0"
    >
      <img 
        src="https://example.com/path-to-your-luxury-car-image.jpg" 
        alt="Luxury detailed car" 
        className="w-full h-full object-cover"
      />
    </motion.div>
    
    <div className="absolute inset-0 bg-gradient-to-b from-transparent to-gray-900 opacity-70"></div>
    
    <div className="absolute inset-0 flex flex-col justify-center items-center">
      <motion.img
        src="https://example.com/path-to-your-logo.png"
        alt="JR Auto Detailing Logo"
        className="w-32 mb-8"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 0.8 }}
      />
      <motion.h1 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7, duration: 0.8 }}
        className="text-4xl md:text-5xl font-light text-white mb-4 text-center"
      >
        JR AUTO DETAILING
      </motion.h1>
      
      <motion.p
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.9, duration: 0.8 }}
        className="text-xl md:text-2xl font-light text-gray-200 mb-8 text-center max-w-2xl"
      >
        INNOVATIVE YET TRUE TO TRADITION
      </motion.p>
    </div>
  </div>
);

const Features = () => (
  <section className="py-20 bg-white">
    <div className="container mx-auto px-4">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <FeatureCard 
          image="https://example.com/path-to-style-image.jpg"
          title="GUIDE TO A UNIQUE STYLE" 
          description="Discover the art of bespoke detailing, where every curve and contour is treated with meticulous care." 
        />
        <FeatureCard 
          image="https://example.com/path-to-interior-image.jpg"
          title="A LUXURIOUS, FUNCTIONAL INTERIOR" 
          description="Experience the pinnacle of automotive comfort with our premium interior detailing services." 
        />
        <FeatureCard 
          image="https://example.com/path-to-evolution-image.jpg"
          title="THE EVOLUTION OF DETAILING" 
          description="Embrace cutting-edge techniques that preserve and enhance your vehicle's pristine condition." 
        />
      </div>
    </div>
