);

const FeatureCard = ({ image, title, description }) => (
  <motion.div 
    initial={{ opacity: 0, y: 20 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.5 }}
    className="bg-gray-100 p-6 rounded-lg shadow-md"
  >
    <img src={image} alt={title} className="w-full h-48 object-cover mb-4 rounded" />
    <h3 className="text-xl font-semibold mb-2">{title}</h3>
    <p className="text-gray-600">{description}</p>
  </motion.div>
);

const Services = () => (
  <section className="py-20 bg-gray-100">
    <div className="container mx-auto px-4">
      <h2 className="text-4xl font-light mb-12 text-center">Our Services</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        <ServiceCard icon={<FaCar />} title="Full Detailing" />
        <ServiceCard icon={<FaSprayCan />} title="Exterior Washing" />
        <ServiceCard icon={<FaBroom />} title="Interior Cleaning" />
        <ServiceCard icon={<FaShieldAlt />} title="Paint Protection" />
      </div>
    </div>
