import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const sections = [
  { id: 'about', title: 'About', subSections: ['Our Story', 'Team', 'Philosophy'] },
  { id: 'services', title: 'Services', subSections: ['Interior', 'Exterior', 'Packages'] },
  { id: 'portfolio', title: 'Portfolio', subSections: ['Gallery', 'Case Studies', 'Testimonials'] },
  { id: 'contact', title: 'Contact', subSections: ['Locations', 'Book Now', 'Support'] },
];

const AboutContent = () => {
  const features = [
    { title: 'Premium Service', description: 'Expert detailing for your vehicle' },
    { title: '5+ Years', description: 'Of industry experience' },
    { title: '100% Satisfaction', description: 'Guaranteed results' },
    { title: 'Eco-Friendly', description: 'Sustainable products used' },
    { title: 'Mobile Service', description: 'We come to you' },
    { title: 'Customized Packages', description: 'Tailored to your needs' },
  ];

  return (
    <div className="flex flex-col min-h-screen justify-center py-16 px-4 sm:px-6 lg:px-8">
      <motion.h1
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.4 }}
        className="text-4xl sm:text-6xl lg:text-8xl font-extrabold mb-8 sm:mb-12 lg:mb-16 text-stone-100 tracking-tight leading-none"
      >
        Elevate Your Ride
      </motion.h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 lg:gap-8">
        {features.map((feature, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 + index * 0.1 }}
            className="p-4 sm:p-6 bg-stone-800 rounded-lg transition-all duration-300 ease-in-out"
            whileHover={{ scale: 1.05, backgroundColor: '#1c1917' }}
          >
            <h3 className="font-bold mb-2 text-stone-100 text-lg sm:text-xl">{feature.title}</h3>
            <p className="text-stone-400 text-sm sm:text-base">{feature.description}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

const PageContent = () => {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={location.pathname}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
        className="min-h-screen"
      >
        <Routes location={location}>
          <Route path="/" element={<AboutContent />} />
          <Route path="/services" element={<ServiceCategories />} />
          <Route path="/portfolio" element={<PortfolioSection />} />
        </Routes>
      </motion.div>
    </AnimatePresence>
  );
};

const Navigation = ({ currentSection, setCurrentSection }) => {
  const [hoveredSection, setHoveredSection] = useState(null);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-stone-900 bg-opacity-90 backdrop-blur-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4 md:justify-start md:space-x-10">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="text-xl font-bold text-stone-100"
          >
            JR Auto Detailing
          </motion.div>
          <div className="hidden md:flex space-x-10">
            {sections.map((section, index) => (
              <div
                key={section.id}
                className="relative"
                onMouseEnter={() => setHoveredSection(index)}
                onMouseLeave={() => setHoveredSection(null)}
              >
                <Link
                  to={section.id === 'about' ? '/' : `/${section.id}`}
                  className={`text-sm font-medium uppercase tracking-wider transition-all duration-300 ${
                    currentSection === index 
                      ? 'text-stone-100' 
                      : 'text-stone-400 hover:text-stone-100'
                  }`}
                  onClick={() => setCurrentSection(index)}
                >
                  {section.title}
                </Link>
                {hoveredSection === index && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute left-0 mt-2 w-48 rounded-md shadow-lg bg-stone-800 ring-1 ring-black ring-opacity-5"
                  >
                    <div className="py-1">
                      {section.subSections.map((subSection, subIndex) => (
                        <Link
                          key={subIndex}
                          to={`/${section.id}/${subSection.toLowerCase().replace(/\s+/g, '-')}`}
                          className="block px-4 py-2 text-sm text-stone-300 hover:bg-stone-700"
                          onClick={() => setCurrentSection(index)}
                        >
                          {subSection}
                        </Link>
                      ))}
                    </div>
                  </motion.div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState(0);

  useEffect(() => {
    const handleResize = () => {
      document.body.style.height = `${window.innerHeight}px`;
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <BrowserRouter>
      <div className="font-sans overflow-hidden bg-stone-900 text-stone-300">
        <Navigation currentSection={currentSection} setCurrentSection={setCurrentSection} />
        <main className="pt-16 sm:pt-20">
          <PageContent />
        </main>
      </div>
    </BrowserRouter>
  );
};

export default AboutPage;
