import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useWheel, useDrag } from '@use-gesture/react';
import ServiceCategories from './ServiceSection';

const sections = [
  { id: 'about', title: 'About', bgColor: 'bg-gray-100', textColor: 'text-gray-800' },
  { id: 'services', title: 'Services', bgColor: 'bg-gray-800', textColor: 'text-gray-100' },
];

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [hoverItem, setHoverItem] = useState(null);

  const goToSection = (index) => {
    if (index >= 0 && index < sections.length && !isTransitioning) {
      setIsTransitioning(true);
      setCurrentSection(index);
      setTimeout(() => setIsTransitioning(false), 800);
    }
  };

  const bindWheel = useWheel(({ direction: [, y], event }) => {
    event.preventDefault();
    if (!isTransitioning) {
      if (y > 0) {
        goToSection(currentSection + 1);
      } else if (y < 0) {
        goToSection(currentSection - 1);
      }
    }
  }, { delay: 200, preventDefault: true });

  const bindDrag = useDrag(({ movement: [, y], direction: [, yDir], cancel }) => {
    if (Math.abs(y) > 50) {
      if (yDir > 0) {
        goToSection(currentSection - 1);
      } else {
        goToSection(currentSection + 1);
      }
      cancel();
    }
  }, { axis: 'y', threshold: 10 });

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowDown') {
        goToSection(currentSection + 1);
      } else if (e.key === 'ArrowUp') {
        goToSection(currentSection - 1);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentSection]);

  const features = [
    { title: 'Premium Service', description: 'Expert detailing for your vehicle' },
    { title: '5+ Years', description: 'Of industry experience' },
    { title: '100% Satisfaction', description: 'Guaranteed results' },
    { title: 'Eco-Friendly', description: 'Sustainable products used' },
    { title: 'Mobile Service', description: 'We come to you' },
    { title: 'Customized Packages', description: 'Tailored to your needs' },
  ];

  const renderSection = (section) => {
    switch (section.id) {
      case 'about':
        return (
          <div className="flex flex-col h-full justify-center">
            <div className="mb-8">
              <span className="text-9xl font-bold text-gray-300">01</span>
            </div>
            <h1 className="text-8xl font-bold mb-12 text-gray-800">JR Auto Detailing.</h1>
            <div className="grid grid-cols-3 gap-8 text-lg">
              {features.map((feature, index) => (
                <motion.div
                  key={index}
                  className="p-4 bg-white rounded-lg shadow-md transition-all duration-300 ease-in-out"
                  whileHover={{ scale: 1.05, backgroundColor: '#f0f0f0' }}
                  onHoverStart={() => setHoverItem(index)}
                  onHoverEnd={() => setHoverItem(null)}
                >
                  <h3 className="font-bold mb-2 text-gray-800">{feature.title}</h3>
                  <p className="text-gray-600">{feature.description}</p>
                  {hoverItem === index && (
                    <motion.div
                      className="mt-2 h-1 bg-blue-500"
                      initial={{ width: 0 }}
                      animate={{ width: '100%' }}
                      transition={{ duration: 0.3 }}
                    />
                  )}
                </motion.div>
              ))}
            </div>
          </div>
        );
      case 'services':
        return <ServiceCategories />;
      default:
        return null;
    }
  };

  return (
    <div 
      className="h-screen overflow-hidden font-sans touch-none bg-gray-100 text-gray-800"
      {...bindWheel()}
      {...bindDrag()}
    >
      <nav className="fixed top-0 left-0 right-0 z-50 flex justify-between items-center p-8 bg-white shadow-md">
        <div className="text-xl font-bold text-gray-800">JR Auto Detailing</div>
        <div className="space-x-4">
          {sections.map((section, index) => (
            <button 
              key={section.id}
              className={`px-4 py-2 rounded-full transition-all duration-300 ${
                currentSection === index 
                  ? 'bg-gray-800 text-white' 
                  : 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              }`}
              onClick={() => goToSection(index)}
            >
              {section.title}
            </button>
          ))}
        </div>
      </nav>

      <AnimatePresence mode="wait">
        <motion.div
          key={currentSection}
          initial={{ opacity: 0, y: 300 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -300 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className={`h-screen ${sections[currentSection].bgColor} ${sections[currentSection].textColor} p-24 flex flex-col justify-center`}
        >
          {renderSection(sections[currentSection])}
        </motion.div>
      </AnimatePresence>

      <div className="fixed bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {sections.map((_, index) => (
          <motion.div
            key={index}
            className={`w-3 h-3 rounded-full ${
              currentSection === index ? 'bg-gray-800' : 'bg-gray-400'
            }`}
            whileHover={{ scale: 1.2 }}
            onClick={() => goToSection(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default AboutPage;
