import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
];

const ServiceCategories = () => {
  const [openCategory, setOpenCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [bookingDetails, setBookingDetails] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingStatus, setBookingStatus] = useState(null);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];

  const handleCategoryClick = (category) => {
    setSelectedType(null);
    setSelectedService(null);
    setBookingDetails(null);
    setShowBookingForm(false);
    setOpenCategory(openCategory === category ? null : category);
  };

  const handleTypeClick = (type) => {
    setSelectedService(null);
    setBookingDetails(null);
    setShowBookingForm(false);
    setSelectedType(selectedType === type ? null : type);
  };

  const handleServiceClick = (service) => {
    setBookingDetails(null);
    setShowBookingForm(false);
    setSelectedService(selectedService === service ? null : service);
  };

  const handleBookingClick = (e, service) => {
    e.stopPropagation();
    setBookingDetails(service);
    setShowBookingForm(true);
  };
  
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const { name, email, date, time } = e.target.elements;
    const bookingInfo = {
      to_email: email.value,
      cc: "timothysuldayan1@gmail.com",
      name: name.value,
      email: email.value,
      date: date.value,
      time: time.value,
      service: bookingDetails.title,
      vehicleType: openCategory,
      price: bookingDetails.prices[openCategory],
      features: bookingDetails.features.join(', '),
      description: bookingDetails.description
    };
  
    setBookingStatus('sending');
  
    emailjs.send('service_co2vfjp', 'template_zjqe62g', bookingInfo, 'y2CeXk4N9lvCdfuzF')
      .then((result) => {
        setBookingStatus('success');
        e.target.reset();
        setBookingDetails(null);
        setShowBookingForm(false);
        setTimeout(() => setBookingStatus(null), 3000);
      }, (error) => {
        setBookingStatus('error');
        console.error('Booking failed:', error);
      });
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1, y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const collapseVariants = {
    hidden: { opacity: 0, height: 0 },
    visible: {
      opacity: 1, height: 'auto',
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    },
    exit: {
      opacity: 0, height: 0,
      transition: {
        duration: 0.6,
        ease: "easeIn"
      }
    }
  };

  return (
    <div className="bg-gray-200 min-h-screen flex items-center justify-center p-8">
      <motion.div
        className="max-w-full w-full"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        {carTypes.map((type, index) => (
          <motion.div
            key={index}
            className="mb-4 border-b border-gray-400 pb-2"
            variants={itemVariants}
          >
            <h2
              className="text-5xl font-bold cursor-pointer flex justify-between items-center pl-8"
              onClick={() => handleCategoryClick(type)}
              style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
            >
              {type.toUpperCase()}
              <span>{openCategory === type ? '-' : '+'}</span>
            </h2>
            <AnimatePresence>
              {openCategory === type && (
                <motion.div
                  initial="hidden"
                  animate="visible"
                  exit="exit"
                  variants={collapseVariants}
                  className="mt-2 pl-8"
                >
                  {['Interior', 'Exterior'].map((serviceType) => (
                    <div key={serviceType} className="mb-2 border-t border-gray-300 pt-2">
                      <h3
                        className="text-4xl font-semibold cursor-pointer flex justify-between items-center pl-4"
                        onClick={() => handleTypeClick(serviceType)}
                        style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
                      >
                        {serviceType}
                        <span>{selectedType === serviceType ? '-' : '+'}</span>
                      </h3>
                      <AnimatePresence>
                        {selectedType === serviceType && (
                          <motion.ul
                            initial="hidden"
                            animate="visible"
                            exit="exit"
                            variants={collapseVariants}
                            className="mt-2 ml-4 border-l border-gray-300 pl-4"
                          >
                            {services.filter(service => service.type === serviceType).map((service, idx) => (
                              <motion.li
                                key={idx}
                                className="py-1 cursor-pointer border-b border-gray-300"
                                onClick={() => handleServiceClick(service)}
                                variants={itemVariants}
                              >
                                <h4 className="text-3xl font-medium flex justify-between items-center pl-2"
                                    style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}>
                                  {service.title.split(' ').pop()}
                                  <span>{selectedService === service ? '-' : '+'}</span>
                                </h4>
                                <AnimatePresence>
                                  {selectedService === service && (
                                    <motion.div
                                      initial="hidden"
                                      animate="visible"
                                      exit="exit"
                                      variants={collapseVariants}
                                      className="mt-2 ml-4"
                                    >
                                      <p className="text-lg">{service.description}</p>
                                      <ul className="list-disc ml-6">
                                        {service.features.map((feature, i) => (
                                          <li key={i}>{feature}</li>
                                        ))}
                                      </ul>
                                      <div className="flex mt-4">
                                        <div>
                                          <h5 className="text-2xl font-bold">Price:</h5>
                                          <p>{type}: {service.prices[type]}</p>
                                          <button
                                            className="mt-2 px-4 py-2 bg-blue-500 text-white font-bold"
                                            onClick={(e) => handleBookingClick(e, service)}
                                          >
                                            Book Now
                                          </button>
                                        </div>
                                        <div className="ml-6">
                                          <img src="https://via.placeholder.com/150" alt={service.title} className="w-48 h-48 object-cover"/>
                                        </div>
                                      </div>
                                    </motion.div>
                                  )}
                                </AnimatePresence>
                              </motion.li>
                            ))}
                          </motion.ul>
                        )}
                      </AnimatePresence>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        ))}
      </motion.div>
      {showBookingForm && bookingDetails && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg w-full max-w-md">
            <h3 className="text-3xl font-bold mb-4">Book {bookingDetails.title}</h3>
            <form onSubmit={handleBookingSubmit}>
              <div className="mb-4">
                <label className="block text-xl mb-2" htmlFor="name">Name:</label>
                <input
                  className="w-full p-2 border border-gray-300 rounded"
                  type="text"
                  id="name"
                  name="name"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-xl mb-2" htmlFor="email">Email:</label>
                <input
                  className="w-full p-2 border border-gray-300 rounded"
                  type="email"
                  id="email"
                  name="email"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-xl mb-2" htmlFor="date">Date:</label>
                <input
                  className="w-full p-2 border border-gray-300 rounded"
                  type="date"
                  id="date"
                  name="date"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-xl mb-2" htmlFor="time">Time:</label>
                <input
                  className="w-full p-2 border border-gray-300 rounded"
                  type="time"
                  id="time"
                  name="time"
                  required
                />
              </div>
              <button
                className="w-full px-4 py-2 bg-green-500 text-white font-bold rounded"
                type="submit"
                disabled={bookingStatus === 'sending'}
              >
                {bookingStatus === 'sending' ? 'Sending...' : 'Submit Booking'}
              </button>
            </form>
            {bookingStatus === 'success' && (
              <p className="mt-4 text-green-600 font-bold">Booking successful!</p>
            )}
            {bookingStatus === 'error' && (
              <p className="mt-4 text-red-600 font-bold">Booking failed. Please try again.</p>
            )}
            <button
              className="mt-4 w-full px-4 py-2 bg-red-500 text-white font-bold rounded"
              onClick={() => setShowBookingForm(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ServiceCategories;
