import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useWheel, useDrag } from '@use-gesture/react';

const sections = [
  { id: 'about', title: 'About.', bgColor: 'bg-cream', textColor: 'text-blue-600' },
  { id: 'stats', title: 'Stats', bgColor: 'bg-blue-600', textColor: 'text-cream' },
  { id: 'commitment', title: 'Our Commitment', bgColor: 'bg-cream', textColor: 'text-blue-600' },
  { id: 'services', title: 'Our Services', bgColor: 'bg-blue-600', textColor: 'text-cream' },
  { id: 'contact', title: 'Contact Us', bgColor: 'bg-cream', textColor: 'text-blue-600' },
];

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  const goToSection = (index) => {
    if (index >= 0 && index < sections.length && !isTransitioning) {
      setIsTransitioning(true);
      setCurrentSection(index);
      setTimeout(() => setIsTransitioning(false), 800); // Match this with your transition duration
    }
  };

  const bindWheel = useWheel(({ direction: [, y], event }) => {
    event.preventDefault();
    if (!isTransitioning) {
      if (y > 0) {
        goToSection(currentSection + 1);
      } else if (y < 0) {
        goToSection(currentSection - 1);
      }
    }
  }, { delay: 200, preventDefault: true });

  const bindDrag = useDrag(({ movement: [, y], direction: [, yDir], cancel }) => {
    if (Math.abs(y) > 50) { // Threshold for drag distance
      if (yDir > 0) {
        goToSection(currentSection - 1);
      } else {
        goToSection(currentSection + 1);
      }
      cancel();
    }
  }, { axis: 'y', threshold: 10 });

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowDown') {
        goToSection(currentSection + 1);
      } else if (e.key === 'ArrowUp') {
        goToSection(currentSection - 1);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentSection]);

  return (
    <div 
      className="h-screen overflow-hidden font-sans touch-none"
      {...bindWheel()}
      {...bindDrag()}
    >
      <nav className="fixed top-0 left-0 right-0 z-50 flex justify-between items-center p-8">
        <div className="text-xl font-bold text-blue-600">JR Auto Detailing</div>
        <div className="space-x-4 text-blue-600">
          <button 
            className="hover:underline"
            onClick={() => setCurrentSection(0)}
          >
            About
          </button>
          <button 
            className="hover:underline"
            onClick={() => setCurrentSection(sections.length - 1)}
          >
            Services
          </button>
        </div>
      </nav>

      <AnimatePresence initial={false}>
        <motion.div
          key={currentSection}
          initial={{ opacity: 0, y: 300 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -300 }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className={`h-screen ${sections[currentSection].bgColor} ${sections[currentSection].textColor} p-24 flex flex-col justify-center`}
        >
          {renderSection(sections[currentSection])}
        </motion.div>
      </AnimatePresence>

      <div className="fixed bottom-4 right-4 space-x-2">
        <button 
          className="bg-blue-600 text-white p-2 rounded"
          onClick={() => goToSection(currentSection - 1)}
          disabled={currentSection === 0}
        >
          Prev
        </button>
        <button 
          className="bg-blue-600 text-white p-2 rounded"
          onClick={() => goToSection(currentSection + 1)}
          disabled={currentSection === sections.length - 1}
        >
          Next
        </button>
      </div>
    </div>
  );
};

const renderSection = (section) => {
  switch (section.id) {
    case 'about':
      return (
        <>
          <h1 className="text-9xl font-bold mb-8">About.</h1>
          <p className="text-2xl">JR Auto Detailing - Your premium car care solution</p>
        </>
      );
    case 'stats':
      return (
        <div className="grid grid-cols-2 gap-8">
          <div>
            <h2 className="text-6xl font-semibold">5+</h2>
            <p className="text-xl">Years of Experience</p>
          </div>
          <div>
            <h2 className="text-6xl font-semibold">1000+</h2>
            <p className="text-xl">Satisfied Customers</p>
          </div>
          <div>
            <h2 className="text-6xl font-semibold">100%</h2>
            <p className="text-xl">Satisfaction Guaranteed</p>
          </div>
          <div>
            <h2 className="text-6xl font-semibold">24/7</h2>
            <p className="text-xl">Customer Support</p>
          </div>
        </div>
      );
    case 'commitment':
      return (
        <>
          <h2 className="text-6xl font-semibold mb-8">Our Commitment</h2>
          <p className="text-xl max-w-2xl">
            JR Auto Detailing is dedicated to providing top-notch car care services. 
            Our expert detailers use state-of-the-art techniques and premium products 
            to restore and maintain your vehicle's pristine condition.
          </p>
        </>
      );
    case 'services':
      return (
        <>
          <h2 className="text-6xl font-semibold mb-8">Our Services</h2>
          <ul className="text-xl grid grid-cols-2 gap-4">
            <li>Interior Detailing</li>
            <li>Exterior Detailing</li>
            <li>Paint Correction</li>
            <li>Ceramic Coating</li>
            <li>Headlight Restoration</li>
            <li>Engine Bay Cleaning</li>
          </ul>
        </>
      );
    case 'contact':
      return (
        <>
          <h2 className="text-6xl font-semibold mb-8">Contact Us</h2>
          <p className="text-xl">Email: info@jrautodetailing.com</p>
          <p className="text-xl">Phone: (555) 123-4567</p>
          <p className="text-xl">Address: 123 Auto Lane, Car City, ST 12345</p>
        </>
      );
    default:
      return null;
  }
};

export default AboutPage;
