import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const sections = [
  { id: 'services', title: 'Services', component: ServiceCategories },
  { id: 'portfolio', title: 'Portfolio', component: PortfolioSection },
  { id: 'contact', title: 'Contact', component: ContactContent },
];

const HomeContent = () => {
  return (
    <div className="flex flex-col min-h-screen justify-center items-center text-white">
      <h1 className="text-6xl font-bold mb-4">Welcome to JR Auto Detailing</h1>
      <p className="text-xl">Experience the finest in automotive care</p>
    </div>
  );
};

const ContactContent = () => {
  return (
    <div className="flex flex-col min-h-screen justify-center items-center text-white">
      <h1 className="text-6xl font-bold mb-4">Contact Us</h1>
      <p className="text-xl">Get in touch for exceptional service</p>
    </div>
  );
};

const Navigation = ({ currentSection, setCurrentSection }) => {
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-black bg-opacity-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          <Link to="/" className="text-xl font-bold text-white">JR Auto Detailing</Link>
          <div className="hidden md:flex space-x-8">
            {sections.map((section) => (
              <Link
                key={section.id}
                to={`/${section.id}`}
                className="text-sm font-medium uppercase tracking-wider text-gray-300 hover:text-white transition-colors duration-300"
                onClick={() => setCurrentSection(section.id)}
              >
                {section.title}
              </Link>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

const PageContent = () => {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={location.pathname}
        initial={{ opacity: 0, x: -100 }}
        animate={{ opacity: 1, x: 0 }}
        exit={{ opacity: 0, x: 100 }}
        transition={{ duration: 0.5 }}
        className="min-h-screen pt-16"
      >
        <Routes location={location}>
          {sections.map((section) => (
            <Route key={section.id} path={`/${section.id}`} element={<section.component />} />
          ))}
          <Route path="/" element={<HomeContent />} />
        </Routes>
      </motion.div>
    </AnimatePresence>
  );
};

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState('home');

  return (
    <BrowserRouter>
      <div className="font-sans bg-gray-900 text-white min-h-screen">
        <Navigation currentSection={currentSection} setCurrentSection={setCurrentSection} />
        <main>
          <PageContent />
        </main>
      </div>
    </BrowserRouter>
  );
};

export default AboutPage;
