import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import AboutPage from './AboutPage';  // Make sure you have these components
import ServicesPage from './ServicesSection';  // Make sure you have these components

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);
  const [showNavbar, setShowNavbar] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  const handleAnimationComplete = () => {
    setTimeout(() => {
      setStep(2);
    }, 3000);
  };

  const handleServiceAnimationComplete = () => {
    setTimeout(() => {
      setShowNavbar(true);
    }, 1000);
  };

  const variants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 0.8, ease: "easeOut" }
    },
    exit: { 
      opacity: 0, 
      y: -50, 
      transition: { duration: 0.8, ease: "easeIn" }
    },
  };

  const serviceBackgroundVariants = {
    hidden: { opacity: 0, y: '100%' },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 1, ease: "easeOut" }
    },
  };

  const renderCurrentPage = () => {
    switch(currentPage) {
      case 'about':
        return <AboutPage />;
      case 'services':
        return <ServicesPage />;
      default:
        return (
          <motion.div
            key="serviceContent"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5, duration: 0.8 }}
            className="absolute inset-0 overflow-auto"
            onAnimationComplete={handleServiceAnimationComplete}
          >
            <div className="text-center mt-16">
              <h1 className="text-6xl font-bold">JR-Auto-Detailing</h1>
              <p className="text-lg mt-4">Located in Surrey, B.C. | Modular Cycling Space for Culture</p>
              <p className="text-sm mt-2">Karl-Marx-Allee 10243 Berlin</p>
            </div>
          </motion.div>
        );
    }
  };

  return (
    <div className="relative w-full h-screen bg-black overflow-hidden">
      <AnimatePresence mode="wait">
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-black"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-white"
            onAnimationComplete={handleAnimationComplete}
          >
            <div className="text-sm mb-2">WELCOME TO</div>
            <div className="text-6xl font-bold mb-2">JR-Auto-Detailing</div>
            <div className="text-lg mb-2">MODULAR CYCLING SPACE FOR CULTURE</div>
            <div className="text-sm">LOCATED IN SURREY, B.C.</div>
          </motion.div>
        )}
        {step === 2 && (
          <>
            <motion.div
              key="serviceBackground"
              initial="hidden"
              animate="visible"
              variants={serviceBackgroundVariants}
              className="absolute inset-0 bg-gray-200"
            />
            {renderCurrentPage()}
          </>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {showNavbar && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="absolute top-0 left-0 right-0 z-10 bg-white p-4"
          >
            <nav className="flex justify-around">
              <button onClick={() => setCurrentPage('home')} className="text-lg font-semibold">Home</button>
              <button onClick={() => setCurrentPage('about')} className="text-lg font-semibold">About</button>
              <button onClick={() => setCurrentPage('services')} className="text-lg font-semibold">Services</button>
            </nav>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
