import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const ServiceCategories = ({ services }) => {
  const [openCategory, setOpenCategory] = useState(null);

  const carTypes = [...new Set(Object.keys(services[0].prices))];
  const serviceTiers = [...new Set(services.map(service => service.title.split(' ').pop()))];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.1 
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <div className="bg-gray-200 min-h-screen p-8">
      <motion.div
        className="max-w-2xl mx-auto"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.div 
          className="mb-4 border-b border-gray-400 pb-2"
          variants={itemVariants}
        >
          <h2 
            className="text-2xl font-bold cursor-pointer"
            onClick={() => setOpenCategory(openCategory === 'carTypes' ? null : 'carTypes')}
          >
            CAR TYPES +
          </h2>
          <AnimatePresence>
            {openCategory === 'carTypes' && (
              <motion.ul
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="mt-2"
              >
                {carTypes.map((type, index) => (
                  <motion.li 
                    key={index}
                    variants={itemVariants}
                    className="py-1"
                  >
                    {type}
                  </motion.li>
                ))}
              </motion.ul>
            )}
          </AnimatePresence>
        </motion.div>

        <motion.div 
          className="mb-4 border-b border-gray-400 pb-2"
          variants={itemVariants}
        >
          <h2 
            className="text-2xl font-bold cursor-pointer"
            onClick={() => setOpenCategory(openCategory === 'serviceTiers' ? null : 'serviceTiers')}
          >
            SERVICE TIERS +
          </h2>
          <AnimatePresence>
            {openCategory === 'serviceTiers' && (
              <motion.ul
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                className="mt-2"
              >
                {serviceTiers.map((tier, index) => (
                  <motion.li 
                    key={index}
                    variants={itemVariants}
                    className="py-1"
                  >
                    {tier}
                  </motion.li>
                ))}
              </motion.ul>
            )}
          </AnimatePresence>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default ServiceCategories;
