import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
];

const ServiceCategories = ({ services }) => {
  const [openCategory, setOpenCategory] = useState(null);
  const [selectedType, setSelectedType] = useState(null);
  const [selectedService, setSelectedService] = useState(null);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];
  const serviceTiers = [...new Set(services.map(service => service.title.split(' ').pop()))];

  const handleCategoryClick = (category) => {
    setSelectedType(null);
    setSelectedService(null);
    setOpenCategory(openCategory === category ? null : category);
  };

  const handleTypeClick = (type) => {
    setSelectedService(null);
    setSelectedType(selectedType === type ? null : type);
  };

  const handleServiceClick = (service) => {
    setSelectedService(selectedService === service ? null : service);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  if (services.length === 0) {
    return <div>No services available.</div>;
  }

  return (
    <div className="bg-gray-200 min-h-screen flex items-center justify-center p-8">
      <motion.div
        className="max-w-full w-full"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        {carTypes.map((type, index) => (
          <motion.div
            key={index}
            className="mb-4 border-b border-gray-400 pb-2"
            variants={itemVariants}
          >
            <h2
              className="text-5xl font-bold cursor-pointer flex justify-between items-center pl-8"
              onClick={() => handleCategoryClick(type)}
              style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
            >
              {type.toUpperCase()}
              <span>{openCategory === type ? '-' : '+'}</span>
            </h2>
            <AnimatePresence>
              {openCategory === type && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  className="mt-2 pl-8"
                >
                  {['Interior', 'Exterior'].map((serviceType) => (
                    <div key={serviceType} className="mb-2 border-t border-gray-300 pt-2">
                      <h3
                        className="text-4xl font-semibold cursor-pointer flex justify-between items-center pl-4"
                        onClick={() => handleTypeClick(serviceType)}
                        style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
                      >
                        {serviceType}
                        <span>{selectedType === serviceType ? '-' : '+'}</span>
                      </h3>
                      <AnimatePresence>
                        {selectedType === serviceType && (
                          <motion.ul
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                            className="mt-2 ml-4 border-l border-gray-300 pl-4"
                          >
                            {services.filter(service => service.type === serviceType).map((service, idx) => (
                              <motion.li
                                key={idx}
                                className="py-1 cursor-pointer border-b border-gray-300"
                                onClick={() => handleServiceClick(service)}
                              >
                                <h4 className="text-3xl font-medium flex justify-between items-center pl-2"
                                    style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}>
                                  {service.title.split(' ').pop()}
                                  <span>{selectedService === service ? '-' : '+'}</span>
                                </h4>
                                <AnimatePresence>
                                  {selectedService === service && (
                                    <motion.div
                                      initial={{ opacity: 0, height: 0 }}
                                      animate={{ opacity: 1, height: 'auto' }}
                                      exit={{ opacity: 0, height: 0 }}
                                      className="mt-2 ml-4"
                                    >
                                      <p className="text-lg">{service.description}</p>
                                      <ul className="list-disc ml-6">
                                        {service.features.map((feature, i) => (
                                          <li key={i}>{feature}</li>
                                        ))}
                                      </ul>
                                      <div className="flex mt-4">
                                        <div>
                                          <h5 className="text-2xl font-bold">Price:</h5>
                                          <p>{type}: {service.prices[type]}</p>
                                        </div>
                                        <div className="ml-6">
                                          <img src="https://via.placeholder.com/150" alt={service.title} className="w-48 h-48 object-cover"/>
                                        </div>
                                      </div>
                                    </motion.div>
                                  )}
                                </AnimatePresence>
                              </motion.li>
                            ))}
                          </motion.ul>
                        )}
                      </AnimatePresence>
                    </div>
                  ))}
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
};

const App = () => {
  return <ServiceCategories services={services} />;
};

export default App;
