import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const sections = [
  { id: 'about', title: 'About', bgColor: 'bg-stone-900', textColor: 'text-stone-300' },
  { id: 'services', title: 'Services', bgColor: 'bg-stone-800', textColor: 'text-stone-200' },
  { id: 'portfolio', title: 'Portfolio', bgColor: 'bg-neutral-200', textColor: 'text-neutral-800' },
];

const AboutContent = () => {
  const features = [
    { title: 'Premium Service', description: 'Expert detailing for your vehicle' },
    { title: '5+ Years', description: 'Of industry experience' },
    { title: '100% Satisfaction', description: 'Guaranteed results' },
    { title: 'Eco-Friendly', description: 'Sustainable products used' },
    { title: 'Mobile Service', description: 'We come to you' },
    { title: 'Customized Packages', description: 'Tailored to your needs' },
  ];

  return (
    <div className="flex flex-col min-h-screen justify-center py-16 px-4 sm:px-6 lg:px-8">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
        className="mb-6 sm:mb-8 lg:mb-12"
      >
        <span className="text-6xl sm:text-7xl lg:text-9xl font-bold text-stone-700 tracking-tighter">01</span>
      </motion.div>
      <motion.h1
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.4 }}
        className="text-4xl sm:text-6xl lg:text-8xl font-bold mb-8 sm:mb-12 lg:mb-16 text-stone-100 tracking-tight"
      >
        JR Auto Detailing.
      </motion.h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 lg:gap-8">
        {features.map((feature, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 + index * 0.1 }}
            className="p-4 sm:p-6 bg-stone-800 rounded-lg transition-all duration-300 ease-in-out"
            whileHover={{ scale: 1.05, backgroundColor: '#1c1917' }}
          >
            <h3 className="font-bold mb-2 text-stone-100 text-lg sm:text-xl">{feature.title}</h3>
            <p className="text-stone-400 text-sm sm:text-base">{feature.description}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

const PageContent = () => {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={location.pathname}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
        className="min-h-screen"
      >
        <Routes location={location}>
          <Route path="/" element={<AboutContent />} />
          <Route path="/services" element={<ServiceCategories />} />
          <Route path="/portfolio" element={<PortfolioSection />} />
        </Routes>
      </motion.div>
    </AnimatePresence>
  );
};

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState(0);

  useEffect(() => {
    const handleResize = () => {
      document.body.style.height = `${window.innerHeight}px`;
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <Router>
      <div className="font-sans overflow-hidden">
        <nav className="fixed top-0 left-0 right-0 z-50 flex flex-col sm:flex-row justify-between items-center p-4 sm:p-8 bg-stone-900 bg-opacity-90 backdrop-blur-sm">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="text-xl font-bold text-stone-100 mb-4 sm:mb-0"
          >
            JR Auto Detailing
          </motion.div>
          <div className="space-x-2 sm:space-x-4">
            {sections.map((section, index) => (
              <motion.div
                key={section.id}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                className="inline-block"
              >
                <Link
                  to={section.id === 'about' ? '/' : `/${section.id}`}
                  className={`px-3 sm:px-4 py-2 text-xs sm:text-sm uppercase tracking-wider transition-all duration-300 ${
                    currentSection === index 
                      ? 'text-stone-100 border-b-2 border-stone-100' 
                      : 'text-stone-400 hover:text-stone-100'
                  }`}
                  onClick={() => setCurrentSection(index)}
                >
                  {section.title}
                </Link>
              </motion.div>
            ))}
          </div>
        </nav>

        <main className={`pt-24 sm:pt-32 ${sections[currentSection].bgColor} ${sections[currentSection].textColor} transition-colors duration-500`}>
          <PageContent />
        </main>
      </div>
    </Router>
  );
};

export default AboutPage;
