import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
];


const ServiceCustomizer = () => {
  const [interiorPackage, setInteriorPackage] = useState(null);
  const [exteriorPackage, setExteriorPackage] = useState(null);
  const [vehicleType, setVehicleType] = useState(null);
  const [showSummary, setShowSummary] = useState(false);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];

  const interiorServices = services.filter(service => service.type === 'Interior');
  const exteriorServices = services.filter(service => service.type === 'Exterior');

  const calculateTotal = () => {
    if (!interiorPackage || !exteriorPackage || !vehicleType) return 0;
    const interiorPrice = parseInt(interiorPackage.prices[vehicleType].replace('$', ''));
    const exteriorPrice = parseInt(exteriorPackage.prices[vehicleType].replace('$', ''));
    return interiorPrice + exteriorPrice;
  };

  const handleBooking = () => {
    // Implement booking logic here
    console.log('Booking:', { interiorPackage, exteriorPackage, vehicleType });
    setShowSummary(true);
  };

  return (
    <div className="bg-stone-800 min-h-screen flex items-center justify-center p-4 sm:p-8 text-stone-200">
      <motion.div
        className="max-w-6xl w-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl sm:text-6xl lg:text-8xl font-bold mb-8 sm:mb-12 lg:mb-16 text-stone-100 tracking-tight">
          Customize Your Detail Package
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <ServiceSelector
            title="Interior Package"
            services={interiorServices}
            selectedPackage={interiorPackage}
            setSelectedPackage={setInteriorPackage}
          />
          <ServiceSelector
            title="Exterior Package"
            services={exteriorServices}
            selectedPackage={exteriorPackage}
            setSelectedPackage={setExteriorPackage}
          />
        </div>

        <div className="mt-12">
          <h2 className="text-2xl font-bold mb-4">Vehicle Type</h2>
          <div className="flex flex-wrap gap-4">
            {carTypes.map((type) => (
              <motion.button
                key={type}
                className={`px-4 py-2 rounded-lg ${
                  vehicleType === type ? 'bg-stone-600' : 'bg-stone-700'
                } hover:bg-stone-600 transition-colors`}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setVehicleType(type)}
              >
                {type}
              </motion.button>
            ))}
          </div>
        </div>

        <motion.div
          className="mt-12 text-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          <h2 className="text-3xl font-bold mb-4">Total: ${calculateTotal()}</h2>
          <motion.button
            className="px-8 py-4 bg-stone-600 text-stone-100 font-bold rounded-lg text-xl hover:bg-stone-500 transition-colors"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleBooking}
            disabled={!interiorPackage || !exteriorPackage || !vehicleType}
          >
            Book Now
          </motion.button>
        </motion.div>

        <AnimatePresence>
          {showSummary && (
            <BookingSummary
              interiorPackage={interiorPackage}
              exteriorPackage={exteriorPackage}
              vehicleType={vehicleType}
              total={calculateTotal()}
              onClose={() => setShowSummary(false)}
            />
          )}
        </AnimatePresence>
      </motion.div>
    </div>
  );
};

const ServiceSelector = ({ title, services, selectedPackage, setSelectedPackage }) => {
  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">{title}</h2>
      <div className="space-y-4">
        {services.map((service) => (
          <motion.div
            key={service.title}
            className={`p-4 rounded-lg cursor-pointer ${
              selectedPackage === service ? 'bg-stone-600' : 'bg-stone-700'
            } hover:bg-stone-600 transition-colors`}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setSelectedPackage(service)}
          >
            <h3 className="text-xl font-semibold">{service.title}</h3>
            <p className="text-sm text-stone-300 mt-2">{service.description}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

const BookingSummary = ({ interiorPackage, exteriorPackage, vehicleType, total, onClose }) => {
  return (
    <motion.div
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <motion.div
        className="bg-stone-800 p-8 rounded-lg max-w-md w-full"
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
      >
        <h2 className="text-2xl font-bold mb-4">Booking Summary</h2>
        <p>Interior Package: {interiorPackage.title}</p>
        <p>Exterior Package: {exteriorPackage.title}</p>
        <p>Vehicle Type: {vehicleType}</p>
        <p className="text-xl font-bold mt-4">Total: ${total}</p>
        <div className="mt-8 flex justify-end">
          <motion.button
            className="px-4 py-2 bg-stone-600 text-stone-100 rounded-lg hover:bg-stone-500 transition-colors"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={onClose}
          >
            Close
          </motion.button>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default ServiceCustomizer;
