// src/components/JRAutoDetailing.js
import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep((prevStep) => (prevStep + 1) % 3);
    }, 3000); // Change step every 3 seconds
    return () => clearTimeout(timer);
  }, [step]);

  const variants = {
    hidden: { opacity: 0, x: 100 },
    visible: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -100 },
  };

  return (
    <div className="relative w-full h-screen bg-gray-900">
      <AnimatePresence>
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-gray-900"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex items-center justify-center"
          >
            <div className="text-center text-white">
              <div className="text-4xl">NEW ORD3R</div>
              <div className="text-sm">A CONCEPT BY BIKEMAILORDER</div>
              <div className="text-lg">MODULAR CYCLING SPACE FOR CULTURE</div>
              <div className="text-sm">KARL-MARX-ALLEE 10243 BERLIN</div>
            </div>
          </motion.div>
        )}
        {step === 2 && (
          <motion.div
            key="services"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex items-center justify-center"
          >
            <div className="text-white text-4xl">Our Services</div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
