import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import axios from 'axios';
import jwt from 'jsonwebtoken';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
];

const ServiceCustomizer = () => {
  const [interiorPackage, setInteriorPackage] = useState(null);
  const [exteriorPackage, setExteriorPackage] = useState(null);
  const [vehicleType, setVehicleType] = useState(null);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingStatus, setBookingStatus] = useState(null);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (token) {
      const decodedToken = jwt.decode(token);
      setUser(decodedToken);
    }
  }, [token]);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];

  const interiorServices = services.filter(service => service.type === 'Interior');
  const exteriorServices = services.filter(service => service.type === 'Exterior');

  const calculateTotal = (interior, exterior, vehicle) => {
    if (!vehicle) return 0;
    const interiorPrice = interior ? parseInt(interior.prices[vehicle].replace('$', '')) : 0;
    const exteriorPrice = exterior ? parseInt(exterior.prices[vehicle].replace('$', '')) : 0;
    return interiorPrice + exteriorPrice;
  };

  const addToCart = () => {
    if (interiorPackage || exteriorPackage) {
      const newItem = {
        id: Date.now(),
        interior: interiorPackage,
        exterior: exteriorPackage,
        vehicleType,
        price: calculateTotal(interiorPackage, exteriorPackage, vehicleType)
      };
      setCart([...cart, newItem]);
      setInteriorPackage(null);
      setExteriorPackage(null);
      setVehicleType(null);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const fetchAvailableSlots = async (date) => {
    try {
      const response = await axios.get(`${API_URL}/available-slots?date=${date}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAvailableSlots(response.data.availableSlots);
    } catch (error) {
      console.error('Error fetching available slots:', error);
    }
  };

  const handleBookingSubmit = async (formData) => {
    try {
      const bookingData = {
        ...formData,
        services: cart.map(item => `${item.interior?.title || ''} ${item.exterior?.title || ''} for ${item.vehicleType}`).join(', '),
        totalPrice: cart.reduce((total, item) => total + item.price, 0),
      };

      setBookingStatus('sending');

      const response = await axios.post(`${API_URL}/book`, bookingData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.status === 201) {
        setBookingStatus('success');
        setCart([]);
        
        setTimeout(() => {
          setBookingStatus(null);
          setShowBookingForm(false);
        }, 3000);
      }
    } catch (error) {
      console.error('Booking failed:', error);
      setBookingStatus(error.response?.data?.message || 'error');
      setTimeout(() => {
        setBookingStatus(null);
      }, 3000);
    }
  };

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="bg-white min-h-screen flex flex-col items-center justify-start p-4 sm:p-8 text-gray-800"
    >
      <main className="w-full max-w-6xl">
        {!user ? (
          <LoginForm setToken={setToken} />
        ) : (
          <>
            <motion.h1 
              initial={{ y: -50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.2, duration: 0.5 }}
              className="text-3xl font-bold mb-8 translate-y-9"
            >
              CHOOSE YOUR SERVICES
            </motion.h1>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4, duration: 0.5 }}
              className="flex mb-8"
            >
              <div className="w-1/2 pr-4">
                <h2 className="text-2xl font-bold mb-4">Interior Service</h2>
                <PackageSelector
                  services={interiorServices}
                  selected={interiorPackage}
                  onSelect={setInteriorPackage}
                />
              </div>
              <div className="w-1/2 pl-4">
                <h2 className="text-2xl font-bold mb-4">Exterior Service</h2>
                <PackageSelector
                  services={exteriorServices}
                  selected={exteriorPackage}
                  onSelect={setExteriorPackage}
                />
              </div>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6, duration: 0.5 }}
              className="mb-8"
            >
              <h2 className="text-2xl font-bold mb-4">Vehicle Type</h2>
              <div className="flex space-x-4">
                {carTypes.map((type) => (
                  <motion.button
                    key={type}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className={`p-2 border rounded-md transition-colors duration-300 ${vehicleType === type ? 'border-black bg-black text-white' : 'border-gray-300'}`}
                    onClick={() => setVehicleType(type)}
                  >
                    {type}
                  </motion.button>
                ))}
              </div>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8, duration: 0.5 }}
              className="mb-8"
            >
              <h2 className="text-2xl font-bold mb-4">Selected Services</h2>
              <div className="flex">
                <div className="w-1/2 pr-4">
                  <h3 className="text-xl font-semibold mb-2">Interior: {interiorPackage ? interiorPackage.title : 'None'}</h3>
                  <FeatureList features={interiorPackage ? interiorPackage.features : []} />
                </div>
                <div className="w-1/2 pl-4">
                  <h3 className="text-xl font-semibold mb-2">Exterior: {exteriorPackage ? exteriorPackage.title : 'None'}</h3>
                  <FeatureList features={exteriorPackage ? exteriorPackage.features : []} />
                </div>
              </div>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 1, duration: 0.5 }}
              className="flex justify-end items-center"
            >
              <motion.span 
                className="text-2xl font-bold mr-4"
                animate={{ scale: [1, 1.1, 1] }}
                transition={{ duration: 0.5, repeat: Infinity, repeatType: "reverse" }}
              >
                ${calculateTotal(interiorPackage, exteriorPackage, vehicleType)}
              </motion.span>
              <motion.button
                className="px-6 py-2 bg-black text-white font-bold mr-4 rounded-md"
                whileHover={{ scale: 1.05, backgroundColor: "#333" }}
                whileTap={{ scale: 0.95 }}
                onClick={addToCart}
                disabled={!interiorPackage && !exteriorPackage || !vehicleType}
              >
                ADD TO CART
              </motion.button>
              <motion.button
                className="px-6 py-2 bg-black text-white font-bold rounded-md"
                whileHover={{ scale: 1.05, backgroundColor: "#333" }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowBookingForm(true)}
                disabled={cart.length === 0}
              >
                BOOK NOW
              </motion.button>
            </motion.div>

            <AnimatePresence>
              {showBookingForm && (
                <BookingForm
                  onSubmit={handleBookingSubmit}
                  onClose={() => setShowBookingForm(false)}
                  bookingStatus={bookingStatus}
                  availableSlots={availableSlots}
                  fetchAvailableSlots={fetchAvailableSlots}
                />
              )}
            </AnimatePresence>

            <motion.div 
              className="fixed bottom-4 right-4"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 1.2, type: "spring", stiffness: 260, damping: 20 }}
            >
              <motion.button
                className="p-3 bg-black text-white rounded-full shadow-lg"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={() => setShowCart(!showCart)}
              >
                🛒 ({cart.length})
              </motion.button>
            </motion.div>

            <AnimatePresence>
              {showCart && (
                <Cart
                  items={cart}
                  onClose={() => setShowCart(false)}
                  onRemove={removeFromCart}
                />
              )}
            </AnimatePresence>
          </>
        )}
      </main>
    </motion.div>
  );
};

const LoginForm = ({ setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_URL}/login`, { email, password });
      localStorage.setItem('token', response.data.token);
      setToken(response.data.token);
    } catch (error) {
      setError('Invalid email or password');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-700">Email</label>
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
        />
      </div>
      <div>
        <label className="block text-sm font-medium text-gray-700">Password</label>
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
        />
      </div>
      <button 
        type="submit"
        className="w-full px-4 py-2 bg-black text-white rounded-md hover:bg-gray-800"
      >
        Login
      </button>
      {error && <p className="text-red-500">{error}</p>}
    </form>
  );
};

const PackageSelector = ({ services, selected, onSelect }) => (
  <div className="space-y-2">
    {services.map((service) => (
      <motion.button
        key={service.title}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
        className={`w-full p-2 border rounded-md transition-colors duration-300 ${selected === service ? 'border-black bg-black text-white' : 'border-gray-300'}`}
        onClick={() => onSelect(service)}
      >
        {service.title}
      </motion.button>
    ))}
  </div>
);

const FeatureList = ({ features }) => (
  <motion.ul 
    className="list-disc pl-5 space-y-1"
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    transition={{ staggerChildren: 0.1 }}
  >
    {features.map((feature, index) => (
      <motion.li 
        key={index}
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
      >
        {feature}
      </motion.li>
    ))}
  </motion.ul>
);

const BookingForm = ({ onSubmit, onClose, bookingStatus, availableSlots, fetchAvailableSlots }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState('');

  useEffect(() => {
    fetchAvailableSlots(selectedDate.toISOString().split('T')[0]);
  }, [selectedDate, fetchAvailableSlots]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = {
      date: selectedDate.toISOString().split('T')[0],
      time: selectedTime
    };
    onSubmit(formData);
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4"
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="bg-white p-8 rounded-lg max-w-md w-full"
      >
        <h2 className="text-2xl font-bold mb-4">Book Your Service</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Name</label>
            <input type="text" name="name" required className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2" />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input type="email" name="email" required className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2" />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Date</label>
            <Calendar
              onChange={setSelectedDate}
              value={selectedDate}
              minDate={new Date()}
              className="w-full"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Time</label>
            <select
              value={selectedTime}
              onChange={(e) => setSelectedTime(e.target.value)}
              required
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
            >
              <option value="">Select a time</option>
              {timeSlots.map(slot => (
                <option key={slot} value={slot}>{slot}</option>
              ))}
            </select>
          </div>
          <div className="flex justify-between">
            <motion.button 
              type="button" 
              onClick={onClose} 
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Cancel
            </motion.button>
            <motion.button 
              type="submit" 
              className="px-4 py-2 bg-black text-white rounded"
              whileHover={{ scale: 1.05, backgroundColor: "#333" }}
              whileTap={{ scale: 0.95 }}
            >
              Book Now
            </motion.button>
          </div>
        </form>
        {bookingStatus === 'sending' && <p className="mt-4 text-blue-600">Sending booking...</p>}
        {bookingStatus === 'success' && <p className="mt-4 text-green-600">Booking successful!</p>}
        {bookingStatus === 'error' && <p className="mt-4 text-red-600">Booking failed. Please try again.</p>}
      </motion.div>
    </motion.div>
  );
};

const Cart = ({ items, onClose, onRemove }) => (
  <motion.div
    initial={{ opacity: 0, x: 300 }}
    animate={{ opacity: 1, x: 0 }}
    exit={{ opacity: 0, x: 300 }}
    className="fixed top-0 right-0 h-full w-80 bg-white shadow-lg p-4 overflow-y-auto"
  >
    <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
    {items.length === 0 ? (
      <p className='translate-y-3'>Your cart is empty</p>
    ) : (
      <ul className="space-y-4">
        {items.map(item => (
          <motion.li 
            key={item.id} 
            className="border-b pb-2"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
          >
            <p>{item.interior?.title || 'No interior'} + {item.exterior?.title || 'No exterior'}</p>
            <p>{item.vehicleType}</p>
            <p>${item.price}</p>
            <motion.button 
              onClick={() => onRemove(item.id)} 
              className="text-red-500"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              Remove
            </motion.button>
          </motion.li>
        ))}
      </ul>
    )}
    <motion.p 
      className="mt-4 font-bold"
      animate={{ scale: [1, 1.05, 1] }}
      transition={{ duration: 0.5, repeat: Infinity, repeatType: "reverse" }}
    >
      Total: ${items.reduce((total, item) => total + item.price, 0)}
    </motion.p>
    <motion.button 
      onClick={onClose} 
      className="mt-4 px-4 py-2 bg-black text-white rounded"
      whileHover={{ scale: 1.05, backgroundColor: "#333" }}
      whileTap={{ scale: 0.95 }}
    >
      Close
    </motion.button>
  </motion.div>
);

export default ServiceCustomizer;
