import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import AboutPage from './AboutPage';

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);
  const [showNavbar, setShowNavbar] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 4000);
    return () => clearTimeout(timer);
  }, []);

  const handleAnimationComplete = () => {
    setTimeout(() => {
      setStep(2);
    }, 1000);
  };

  const handleServiceAnimationComplete = () => {
    setTimeout(() => {
      setShowNavbar(true);
    }, 1000);
  };

  const variants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1, 
      transition: { duration: 1.5, ease: "easeOut" }
    },
    exit: { 
      opacity: 0, 
      transition: { duration: 1, ease: "easeIn" }
    },
  };

  const textVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { 
      opacity: 1, 
      x: 0, 
      transition: { duration: 0.8, ease: "easeOut", delay: 0.5 }
    },
  };

  const carVariants = {
    hidden: { opacity: 0, scale: 0.95 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { duration: 1.5, ease: "easeOut" }
    },
    exit: {
      opacity: 0,
      scale: 1.05,
      transition: { duration: 1, ease: "easeIn" }
    }
  };

  return (
    <div className="relative w-full h-screen bg-black overflow-hidden font-sans">
      <AnimatePresence mode="wait">
        {step === 0 && (
          <motion.div
            key="opening"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-white"
            onAnimationComplete={handleAnimationComplete}
          >
            <motion.div 
              variants={textVariants}
              className="absolute top-8 right-8 text-3xl font-bold tracking-wider"
            >
              JR AUTO DETAILING
            </motion.div>
            <motion.div
              variants={carVariants}
              className="w-full max-w-4xl h-64 md:h-96 relative"
            >
              <img
                src="./blackCar.jpg" // Replace with your car image
                alt="Detailed car"
                className="w-full h-full object-cover object-center"
              />
            </motion.div>
            <motion.div 
              variants={textVariants}
              className="text-xl tracking-wide absolute bottom-8 right-8"
            >
              ELEVATE YOUR RIDE
            </motion.div>
          </motion.div>
        )}
        {step === 1 && (
          <motion.div
            key="transition"
            initial={{ opacity: 1 }}
            animate={{ opacity: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
            className="absolute inset-0 bg-black"
            onAnimationComplete={() => setStep(2)}
          />
        )}
        {step === 2 && (
          <motion.div
            key="aboutPage"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
            className="absolute inset-0 overflow-auto"
            onAnimationComplete={handleServiceAnimationComplete}
          >
            <AboutPage />
          </motion.div>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {showNavbar && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="absolute top-0 left-0 right-0 z-10 p-6"
          >
            {/* Your navbar content */}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
