import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
  ];

  const serviceOptions = ["Silver", "Gold", "Platinum"];
  const carTypes = ["Coupe/Sedan", "SUV/Pickup Truck", "Large SUV/Lifted Pickup Truck"];
  
  const ServiceSection = () => {
    const [selectedOption, setSelectedOption] = useState(null);
    const [selectedCarType, setSelectedCarType] = useState(null);
  
    const handleOptionClick = (option) => {
      setSelectedOption(option);
    };
  
    const handleCarTypeClick = (carType) => {
      setSelectedCarType(carType);
    };
  
    const handleBackClick = () => {
      if (selectedCarType) {
        setSelectedCarType(null);
      } else {
        setSelectedOption(null);
      }
    };
  
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold">Our Services</h1>
        </div>
        <AnimatePresence>
          {!selectedOption && (
            <motion.div
              key="overview"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="grid grid-cols-1 gap-6"
            >
              {serviceOptions.map(option => (
                <motion.div
                  key={option}
                  className="relative cursor-pointer"
                  onClick={() => handleOptionClick(option)}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                >
                  <img
                    src={`/images/${option.toLowerCase()}.jpg`}
                    alt={option}
                    className="w-full h-64 object-cover opacity-50"
                  />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <h2 className="text-3xl font-bold text-white">{option}</h2>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}
          {selectedOption && !selectedCarType && (
            <motion.div
              key="carTypes"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="grid grid-cols-1 gap-6"
            >
              <button
                onClick={handleBackClick}
                className="col-span-1 bg-gray-200 p-2 rounded mb-6"
              >
                Back to Options
              </button>
              {carTypes.map(carType => (
                <motion.div
                  key={carType}
                  className="relative cursor-pointer"
                  onClick={() => handleCarTypeClick(carType)}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                >
                  <img
                    src={`/images/${carType.toLowerCase().replace(/ /g, '_')}.jpg`}
                    alt={carType}
                    className="w-full h-64 object-cover opacity-50"
                  />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <h2 className="text-3xl font-bold text-white">{carType}</h2>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}
          {selectedOption && selectedCarType && (
            <motion.div
              key="details"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
            >
              <button
                onClick={handleBackClick}
                className="col-span-1 md:col-span-2 lg:col-span-3 bg-gray-200 p-2 rounded mb-6"
              >
                Back to Car Types
              </button>
              {services
                .filter(service => service.title.includes(selectedOption))
                .map((service, index) => (
                  <div key={index} className="bg-white shadow-md rounded-lg overflow-hidden">
                    <img
                      src={`/images/${service.type.toLowerCase()}.jpg`}
                      alt={service.title}
                      className="w-full h-48 object-cover"
                    />
                    <div className="p-4">
                      <h2 className="text-xl font-semibold">{service.title}</h2>
                      <p className="text-gray-600">{service.description}</p>
                      <ul className="mt-2">
                        {service.features.map((feature, idx) => (
                          <li key={idx} className="text-gray-800">- {feature}</li>
                        ))}
                      </ul>
                      <div className="mt-4">
                        {Object.entries(service.prices)
                          .filter(([type]) => type === selectedCarType)
                          .map(([type, price], idx) => (
                            <p key={idx} className="text-gray-800">{type}: {price}</p>
                          ))}
                      </div>
                    </div>
                  </div>
                ))}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    );
  };
  
  export default ServiceSection;
