import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import AboutPage from './AboutPage';

const JRAutoDetailing = () => {
  const [currentSection, setCurrentSection] = useState('hero');

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentSection('about');
    }, 5500); // 5.5 seconds for hero animation

    return () => clearTimeout(timer);
  }, []);

  const sectionVariants = {
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    exit: { opacity: 0 }
  };

  const contentVariants = {
    initial: { opacity: 0, y: 20 },
    animate: { opacity: 1, y: 0 }
  };

  return (
    <div className="relative w-full h-screen overflow-hidden bg-stone-900">
      <AnimatePresence mode="wait">
        {currentSection === 'hero' ? (
          <motion.div
            key="hero"
            variants={sectionVariants}
            initial="initial"
            animate="animate"
            exit="exit"
            transition={{ duration: 1 }}
            className="absolute inset-0 flex flex-col items-center justify-center text-stone-100"
          >
            <motion.div 
              variants={contentVariants}
              transition={{ delay: 0.5, duration: 0.8 }}
              className="text-sm mb-4 tracking-widest"
            >
              LOCATED IN SURREY, B.C.
            </motion.div>
            <motion.div 
              variants={contentVariants}
              transition={{ delay: 0.7, duration: 0.8 }}
              className="text-7xl font-bold mb-6 tracking-tight"
              style={{ fontFamily: "'Helvetica Neue', Arial, sans-serif" }}
            >
              JR AUTO DETAILING
            </motion.div>
            <motion.div 
              variants={contentVariants}
              transition={{ delay: 0.9, duration: 0.8 }}
              className="text-xl mb-4 tracking-wide"
            >
              PREMIUM CAR CARE EXPERIENCE
            </motion.div>
            <motion.div 
              variants={contentVariants}
              transition={{ delay: 1.1, duration: 0.8 }}
              className="text-sm tracking-widest"
            >
              SERVING THE LOWER MAINLAND
            </motion.div>
          </motion.div>
        ) : (
          <motion.div
            key="about"
            variants={sectionVariants}
            initial="initial"
            animate="animate"
            exit="exit"
            transition={{ duration: 1 }}
            className="absolute inset-0"
          >
            <AboutPage />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
