import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from './Navbar';
import ServiceCategories from './ServiceSection';

const App = () => {
  const [step, setStep] = useState(0);
  const [isWhiteBackground, setIsWhiteBackground] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  const handleBackgroundChange = () => {
    setIsWhiteBackground(true);
  };

  const variants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 0.8, ease: "easeOut" }
    },
    exit: { 
      opacity: 0, 
      y: -50, 
      transition: { duration: 0.8, ease: "easeIn" }
    },
  };

  const serviceBackgroundVariants = {
    hidden: { opacity: 0, y: '100%' },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { duration: 1, ease: "easeOut" }
    },
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <Navbar isWhiteBackground={isWhiteBackground} />
      
      <AnimatePresence mode="wait">
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-black"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-white"
            onAnimationComplete={() => {
              setTimeout(() => setStep(2), 5000);
            }}
          >
            <div className="text-sm mb-2">LOCATED IN SURREY, B.C.</div>
            <div className="text-6xl font-bold mb-2">JR-AUTO-DETAILING</div>
            <div className="text-lg mb-2">MODULAR CYCLING SPACE FOR CULTURE</div>
            <div className="text-sm">KARL-MARX-ALLEE 10243 BERLIN</div>
          </motion.div>
        )}
        {step === 2 && (
          <>
            <motion.div
              key="serviceBackground"
              initial="hidden"
              animate="visible"
              variants={serviceBackgroundVariants}
              className="absolute inset-0 bg-white"
              onAnimationComplete={handleBackgroundChange}
            >
              <motion.div
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.5, duration: 0.8 }}
                className="absolute inset-0 flex items-center justify-center"
              >
                <div className="text-black text-6xl font-bold">Our Services</div>
              </motion.div>
            </motion.div>
            <motion.div
              key="serviceContent"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 1.5, duration: 0.8 }}
              className="absolute inset-0 overflow-auto"
            >
              <ServiceCategories />
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </div>
  );
};

export default App;
