import React, { useState, useEffect } from 'react';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
  ];

  const calendlyLink = "https://calendly.com/timothysuldayan1/detailing-booking";

const ServiceSection = () => {
  const [serviceFilter, setServiceFilter] = useState('All');
  const [carTypeFilter, setCarTypeFilter] = useState('All');
  const [selectedService, setSelectedService] = useState(null);

  const filteredServices = services.filter(service => 
    (serviceFilter === 'All' || service.type === serviceFilter) &&
    (carTypeFilter === 'All' || service.carTypes.includes(carTypeFilter))
  );

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://assets.calendly.com/assets/external/widget.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="bg-gray-50 min-h-screen p-8">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800 mb-2">Our Services</h1>
        <p className="text-lg text-gray-600">Choose the perfect detailing package for your vehicle.</p>
      </div>
      <div className="flex justify-center mb-8">
        <button 
          className={`mx-2 px-4 py-2 rounded ${serviceFilter === 'All' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setServiceFilter('All')}
        >
          All Services
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${serviceFilter === 'Interior' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setServiceFilter('Interior')}
        >
          Interior
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${serviceFilter === 'Exterior' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setServiceFilter('Exterior')}
        >
          Exterior
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${carTypeFilter === 'All' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setCarTypeFilter('All')}
        >
          All Car Types
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${carTypeFilter === 'Coupe/Sedan' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setCarTypeFilter('Coupe/Sedan')}
        >
          Coupe/Sedan
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${carTypeFilter === 'SUV/Pickup Truck' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setCarTypeFilter('SUV/Pickup Truck')}
        >
          SUV/Pickup Truck
        </button>
        <button 
          className={`mx-2 px-4 py-2 rounded ${carTypeFilter === 'Large SUV/Lifted Pickup Truck' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`} 
          onClick={() => setCarTypeFilter('Large SUV/Lifted Pickup Truck')}
        >
          Large SUV/Lifted Pickup Truck
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-8">
        {filteredServices.map((service, index) => (
          <div key={index} className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">{service.title}</h2>
            <p className="text-gray-600 mb-4">{service.description}</p>
            <ul className="list-disc list-inside mb-4">
              {service.features.map((feature, idx) => (
                <li key={idx} className="text-gray-600">{feature}</li>
              ))}
            </ul>
            <div className="text-gray-800 font-semibold mb-4">
              {Object.entries(service.prices).map(([type, price]) => (
                <div key={type}>
                  {type}: {price}
                </div>
              ))}
            </div>
            <div className="text-center">
              <button 
                onClick={() => setSelectedService(service)}
                className="bg-blue-600 text-white px-4 py-2 rounded"
              >
                Book Now
              </button>
            </div>
          </div>
        ))}
      </div>
      {selectedService && (
        <div className="text-center mt-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-4">Book an Appointment</h2>
          <div className="calendly-inline-widget" data-url={calendlyLink} style={{ minWidth: '320px', height: '700px' }}></div>
        </div>
      )}
    </div>
  );
};

export default ServiceSection;
