import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { FaBars, FaTimes } from 'react-icons/fa';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <motion.nav 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
      className="bg-white p-4 flex justify-between items-center fixed w-full z-10 shadow-md"
    >
      <Link to="/" className="text-2xl font-light text-gray-800">JR Auto Detailing</Link>
      <div className="hidden md:flex items-center space-x-6">
        <NavLink to="/about">About</NavLink>
        <NavLink to="/portfolio">Portfolio</NavLink>
        <NavLink to="/services">Services</NavLink>
        <motion.button 
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="bg-black text-white px-4 py-2 rounded text-sm uppercase tracking-wide"
        >
          Contact
        </motion.button>
      </div>
      <button className="md:hidden text-gray-800" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </button>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.2 }}
            className="absolute top-full left-0 right-0 bg-white shadow-md p-4 md:hidden"
          >
            <NavLink to="/about" onClick={() => setIsOpen(false)}>About</NavLink>
            <NavLink to="/portfolio" onClick={() => setIsOpen(false)}>Portfolio</NavLink>
            <NavLink to="/services" onClick={() => setIsOpen(false)}>Services</NavLink>
            <motion.button 
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="bg-black text-white px-4 py-2 rounded text-sm uppercase tracking-wide mt-4 w-full"
            >
              Contact
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
};

const NavLink = ({ to, children, onClick }) => (
  <Link 
    to={to} 
    className="block md:inline text-sm uppercase tracking-wide text-gray-800 hover:text-gray-600 transition-colors duration-300 py-2 md:py-0"
    onClick={onClick}
  >
    {children}
  </Link>
);

export default Navbar;
