import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, useAnimation } from 'framer-motion';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const sections = [
  { id: 'about', title: 'About', subSections: ['Our Story', 'Team', 'Philosophy'] },
  { id: 'services', title: 'Services', subSections: ['Interior', 'Exterior', 'Packages'] },
  { id: 'portfolio', title: 'Portfolio', subSections: ['Gallery', 'Case Studies', 'Testimonials'] },
  { id: 'contact', title: 'Contact', subSections: ['Locations', 'Book Now', 'Support'] },
];

const features = [
  { title: 'Premium Service', description: 'Expert detailing for your vehicle', position: { x: 1, y: 0.5, z: 0 } },
  { title: '5+ Years', description: 'Of industry experience', position: { x: -1, y: 0.5, z: 0 } },
  { title: '100% Satisfaction', description: 'Guaranteed results', position: { x: 0, y: 1, z: 0 } },
  { title: 'Eco-Friendly', description: 'Sustainable products used', position: { x: 0, y: 0, z: 1 } },
  { title: 'Mobile Service', description: 'We come to you', position: { x: 0.5, y: -0.5, z: 0.5 } },
  { title: 'Customized Packages', description: 'Tailored to your needs', position: { x: -0.5, y: -0.5, z: 0.5 } },
];

const CarModel = ({ setHoveredFeature }) => {
  const mount = useRef(null);
  const [isAnimating, setAnimating] = useState(true);

  useEffect(() => {
    let width = mount.current.clientWidth;
    let height = mount.current.clientHeight;
    let frameId;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });

    renderer.setSize(width, height);
    mount.current.appendChild(renderer.domElement);

    // Add car model here
    const geometry = new THREE.BoxGeometry(1, 0.5, 2);
    const material = new THREE.MeshPhongMaterial({ color: 0x000000 });
    const car = new THREE.Mesh(geometry, material);
    scene.add(car);

    // Add lights
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);
    const pointLight = new THREE.PointLight(0xffffff, 1);
    pointLight.position.set(5, 5, 5);
    scene.add(pointLight);

    camera.position.z = 5;

    // Add feature indicators
    features.forEach((feature, index) => {
      const indicatorGeometry = new THREE.SphereGeometry(0.05, 32, 32);
      const indicatorMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
      const indicator = new THREE.Mesh(indicatorGeometry, indicatorMaterial);
      indicator.position.set(feature.position.x, feature.position.y, feature.position.z);
      car.add(indicator);

      indicator.userData = { featureIndex: index };
    });

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.enableZoom = false;

    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2();

    const onMouseMove = (event) => {
      mouse.x = (event.clientX / width) * 2 - 1;
      mouse.y = -(event.clientY / height) * 2 + 1;

      raycaster.setFromCamera(mouse, camera);
      const intersects = raycaster.intersectObjects(car.children);

      if (intersects.length > 0) {
        const featureIndex = intersects[0].object.userData.featureIndex;
        setHoveredFeature(features[featureIndex]);
      } else {
        setHoveredFeature(null);
      }
    };

    mount.current.addEventListener('mousemove', onMouseMove);

    const animate = () => {
      if (isAnimating) {
        car.rotation.y += 0.01;
      }

      controls.update();
      renderer.render(scene, camera);
      frameId = window.requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.cancelAnimationFrame(frameId);
      mount.current.removeEventListener('mousemove', onMouseMove);
      mount.current.removeChild(renderer.domElement);
    };
  }, [isAnimating, setHoveredFeature]);

  return <div ref={mount} style={{ width: '100%', height: '400px' }} />;
};

const AboutContent = () => {
  const [hoveredFeature, setHoveredFeature] = useState(null);
  const controls = useAnimation();

  useEffect(() => {
    controls.start({ opacity: 1, y: 0 });
  }, [controls]);

  return (
    <div className="flex flex-col min-h-screen justify-center py-16 px-4 sm:px-6 lg:px-8 bg-gradient-to-b from-gray-900 to-black">
      <motion.h1
        initial={{ opacity: 0, y: 50 }}
        animate={controls}
        transition={{ duration: 0.8, delay: 0.4 }}
        className="text-4xl sm:text-6xl lg:text-8xl font-extrabold mb-8 sm:mb-12 lg:mb-16 text-white tracking-tight leading-none"
      >
        Elevate Your Ride
      </motion.h1>
      <div className="relative">
        <CarModel setHoveredFeature={setHoveredFeature} />
        <AnimatePresence>
          {hoveredFeature && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 20 }}
              className="absolute top-0 left-0 bg-black bg-opacity-80 text-white p-4 rounded-lg"
            >
              <h3 className="text-xl font-bold mb-2">{hoveredFeature.title}</h3>
              <p>{hoveredFeature.description}</p>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

const PageContent = () => {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={location.pathname}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
        className="min-h-screen"
      >
        <Routes location={location}>
          <Route path="/" element={<AboutContent />} />
          <Route path="/services" element={<ServiceCategories />} />
          <Route path="/portfolio" element={<PortfolioSection />} />
        </Routes>
      </motion.div>
    </AnimatePresence>
  );
};

const Navigation = ({ currentSection, setCurrentSection }) => {
  const [hoveredSection, setHoveredSection] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${isScrolled ? 'bg-black' : 'bg-transparent'}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4 md:justify-start md:space-x-10">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="text-xl font-bold text-white"
          >
            JR Auto Detailing
          </motion.div>
          <div className="hidden md:flex space-x-10">
            {sections.map((section, index) => (
              <div
                key={section.id}
                className="relative"
                onMouseEnter={() => setHoveredSection(index)}
                onMouseLeave={() => setHoveredSection(null)}
              >
                <Link
                  to={section.id === 'about' ? '/' : `/${section.id}`}
                  className={`text-sm font-medium uppercase tracking-wider transition-all duration-300 ${
                    currentSection === index 
                      ? 'text-white' 
                      : 'text-gray-400 hover:text-white'
                  }`}
                  onClick={() => setCurrentSection(index)}
                >
                  {section.title}
                </Link>
                {hoveredSection === index && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute left-0 mt-2 w-48 rounded-md shadow-lg bg-black bg-opacity-90 ring-1 ring-white ring-opacity-5"
                  >
                    <div className="py-1">
                      {section.subSections.map((subSection, subIndex) => (
                        <Link
                          key={subIndex}
                          to={`/${section.id}/${subSection.toLowerCase().replace(/\s+/g, '-')}`}
                          className="block px-4 py-2 text-sm text-gray-300 hover:bg-gray-800"
                          onClick={() => setCurrentSection(index)}
                        >
                          {subSection}
                        </Link>
                      ))}
                    </div>
                  </motion.div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

const AboutPage = () => {
  const [currentSection, setCurrentSection] = useState(0);

  useEffect(() => {
    const handleResize = () => {
      document.body.style.height = `${window.innerHeight}px`;
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <BrowserRouter>
      <div className="font-sans overflow-hidden bg-black text-white">
        <Navigation currentSection={currentSection} setCurrentSection={setCurrentSection} />
        <main className="pt-16 sm:pt-20">
          <PageContent />
        </main>
      </div>
    </BrowserRouter>
  );
};

export default AboutPage;
