import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { FaCar, FaSprayCan, FaBroom, FaShieldAlt, FaPhone, FaEnvelope, FaMapMarkerAlt, FaSearch, FaGlobe, FaUser } from 'react-icons/fa';
import ServiceCategories from './ServiceSection';
import PortfolioSection from './PortfolioSection';

const AboutContent = () => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <Hero />
      <Features />
      <Services />
      <Contact />
    </div>
  );
};

const Hero = () => (
  <div className="relative h-screen bg-gray-200 overflow-hidden flex flex-col">
    <div className="container mx-auto px-4 flex-grow flex flex-col justify-center items-center">
      <motion.h1 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 0.8 }}
        className="text-4xl md:text-5xl font-light text-gray-800 mb-4 text-center uppercase tracking-wide"
      >
        JR Auto Detailing
      </motion.h1>
      
      <motion.p
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7, duration: 0.8 }}
        className="text-xl md:text-2xl font-light text-gray-600 mb-8 text-center uppercase tracking-wider"
      >
        Innovative yet true to tradition
      </motion.p>
      
      <motion.div 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
        className="w-full max-w-4xl aspect-[16/9] relative"
      >
        <img 
          src="https://pngimg.com/uploads/bugatti/bugatti_PNG16.png" 
          alt="Luxury detailed car" 
          className="w-full h-full object-contain"
        />
      </motion.div>
      
      <motion.p
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.9, duration: 0.8 }}
