import React from 'react';
import { motion } from 'framer-motion';

const AboutPage = () => {
  return (
    <div className="bg-white min-h-screen pt-20">
      <motion.div 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="container mx-auto px-4 py-12"
      >
        <main className="flex flex-col lg:flex-row items-center">
          <motion.div 
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.4 }}
            className="lg:w-1/2 pr-0 lg:pr-8 mb-8 lg:mb-0"
          >
            <h1 className="text-4xl lg:text-5xl font-bold mb-6">Premium Car Detailing</h1>
            <p className="text-lg lg:text-xl text-gray-600 mb-8">
              Elevate your vehicle's appearance with our expert detailing services. We are passionate about delivering the highest quality care for your prized possession.
            </p>
            <ul className="space-y-4 mb-8">
              <li className="flex items-center">
                <span className="w-32 text-gray-600">Location</span>
                <span className="font-semibold text-xl lg:text-2xl">Surrey, B.C.</span>
              </li>
              <li className="flex items-center">
                <span className="w-32 text-gray-600">Satisfaction</span>
                <span className="font-semibold text-xl lg:text-2xl">100% guaranteed</span>
              </li>
              <li className="flex items-center">
                <span className="w-32 text-gray-600">Expertise</span>
                <span className="font-semibold text-xl lg:text-2xl">Interior & Exterior</span>
              </li>
            </ul>
          </motion.div>

          <motion.div 
            initial={{ x: 50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.6 }}
            className="lg:w-1/2 w-full relative"
            style={{ height: '300px', maxWidth: '600px', margin: '0 auto' }}
          >
            <div 
              className="absolute inset-0 bg-cover bg-center rounded-lg"
              style={{ 
                backgroundImage: "url('https://pendryresidencesweho.com/wp-content/uploads/2020/12/The-Best-Penthouse-and-Private-Garage-in-West-Hollywood-CA-1536x833.jpg')",
                backgroundSize: 'cover',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
              }}
            />
            <img 
              src="https://pngimg.com/uploads/porsche/porsche_PNG102866.png" 
              alt="Luxury Car" 
              className="absolute bottom-0 left-0"
              style={{ 
                height: '80%',
                width: 'auto',
                maxWidth: 'none',
                transform: 'translateX(-35%) translateY(10%)',
                filter: 'drop-shadow(0 4px 6px rgba(0, 0, 0, 0.2))'
              }}
            />
          </motion.div>
        </main>

        <motion.div 
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.8 }}
          className="mt-20"
        >
          <h2 className="text-2xl lg:text-3xl font-semibold mb-8 text-center">Why Choose Us</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[
              { title: 'Attention to Detail', description: 'We meticulously care for every inch of your vehicle.' },
              { title: 'Eco-Friendly Products', description: 'Our products are safe for your car and the environment.' },
              { title: 'Customized Service', description: 'Tailored detailing packages to meet your specific needs.' }
            ].map((item, index) => (
              <div key={index} className="bg-gray-50 p-6 rounded-lg shadow-lg text-center">
                <h3 className="text-lg lg:text-xl font-semibold mb-4">{item.title}</h3>
                <p className="text-sm lg:text-base text-gray-600">{item.description}</p>
              </div>
            ))}
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default AboutPage;
