import React, { useState } from 'react';
import { motion } from 'framer-motion';

const PortfolioSection = () => {
    const [selectedWork, setSelectedWork] = useState(null);
    const portfolioItems = [
      { id: 1, year: '2023', title: 'Luxury Sedan Detailing', image: '/path/to/image1.jpg' },
      { id: 2, year: '2023', title: 'Sports Car Restoration', image: '/path/to/image2.jpg' },
      { id: 3, year: '2022', title: 'Classic Car Preservation', image: '/path/to/image3.jpg' },
    ];
  
    return (
      <div className="flex flex-col h-full justify-center">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="mb-6 sm:mb-8 lg:mb-12"
        >
          <span className="text-6xl sm:text-7xl lg:text-9xl font-bold text-stone-600 tracking-tighter">03</span>
        </motion.div>
        <motion.h2
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="text-4xl sm:text-6xl lg:text-8xl font-bold mb-8 sm:mb-12 lg:mb-16 text-stone-100 tracking-tight"
        >
          Our Work.
        </motion.h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 lg:gap-8">
          {portfolioItems.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.6 + index * 0.1 }}
              className="relative aspect-square overflow-hidden rounded-lg cursor-pointer"
              whileHover={{ scale: 1.05 }}
              onClick={() => setSelectedWork(item)}
            >
              <img src={item.image} alt={item.title} className="object-cover w-full h-full" />
              <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col justify-end p-4">
                <span className="text-stone-300 text-sm">{item.year}</span>
                <h3 className="text-stone-100 text-xl font-bold">{item.title}</h3>
              </div>
            </motion.div>
          ))}
        </div>
        {selectedWork && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50"
            onClick={() => setSelectedWork(null)}
          >
            <div className="bg-stone-800 p-8 rounded-lg max-w-3xl w-full">
              <img src={selectedWork.image} alt={selectedWork.title} className="w-full h-auto mb-4 rounded" />
              <h3 className="text-2xl font-bold text-stone-100 mb-2">{selectedWork.title}</h3>
              <p className="text-stone-300">{selectedWork.year}</p>
            </div>
          </motion.div>
        )}
      </div>
    );
  };

  export default PortfolioSection;
