import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
];


const ServiceCustomizer = () => {
  const [selectedService, setSelectedService] = useState('interior');
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [vehicleType, setVehicleType] = useState(null);

  const carTypes = services.length > 0 ? [...new Set(Object.keys(services[0].prices))] : [];

  const filteredServices = services.filter(service => service.type.toLowerCase() === selectedService);

  const calculatePrice = () => {
    if (!selectedPackage || !vehicleType) return '0.00';
    return selectedPackage.prices[vehicleType].replace('$', '');
  };

  return (
    <div className="bg-white min-h-screen flex flex-col items-center justify-start p-4 sm:p-8 text-gray-800">
      <header className="w-full max-w-6xl flex justify-between items-center mb-8">
        <div className="text-2xl font-bold">Logo</div>
        <nav className="flex space-x-4">
          <a href="#" className="text-gray-600 hover:text-gray-800">CUSTOM</a>
          <a href="#" className="text-gray-600 hover:text-gray-800">LOOKBOOK</a>
          <a href="#" className="text-gray-600 hover:text-gray-800">ABOUT US</a>
        </nav>
        <div className="flex items-center space-x-4">
          <button>üá¨üáß</button>
          <button>‚ù§Ô∏è</button>
          <button>üõí</button>
        </div>
      </header>

      <main className="w-full max-w-6xl">
        <h1 className="text-3xl font-bold mb-8">CHOOSE YOUR SERVICE</h1>

        <div className="flex mb-8">
          <div className="w-1/4 space-y-4">
            <ServiceTypeSelector
              title="Service Type"
              options={['Interior', 'Exterior']}
              selected={selectedService}
              onSelect={(type) => setSelectedService(type.toLowerCase())}
            />
            <PackageSelector
              title="Package"
              services={filteredServices}
              selected={selectedPackage}
              onSelect={setSelectedPackage}
            />
            <VehicleTypeSelector
              title="Vehicle Type"
              types={carTypes}
              selected={vehicleType}
              onSelect={setVehicleType}
            />
          </div>

          <div className="w-1/2 px-8">
            <ServiceVisualizer service={selectedPackage} />
          </div>

          <div className="w-1/4">
            <FeatureList features={selectedPackage?.features || []} />
          </div>
        </div>

        <div className="flex justify-between items-center">
          <button className="text-gray-600 hover:text-gray-800 flex items-center">
            <span className="mr-2">‚ô°</span> Save for later
          </button>
          <div className="flex items-center">
            <span className="text-2xl font-bold mr-4">{calculatePrice()} ‚Ç¨</span>
            <motion.button
              className="px-6 py-2 bg-black text-white font-bold"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => console.log('Booking:', { selectedService, selectedPackage, vehicleType })}
            >
              BUY
            </motion.button>
          </div>
        </div>
      </main>

      <footer className="w-full max-w-6xl mt-12 flex justify-between text-sm text-gray-600">
        <div>
          <h3 className="font-bold mb-2">Customer Service</h3>
          <ul>
            <li>About</li>
            <li>Retailers</li>
          </ul>
        </div>
        <div className="flex space-x-4">
          <a href="#">Facebook</a>
          <a href="#">Instagram</a>
          <a href="#">Twitter</a>
          <a href="#">Pinterest</a>
        </div>
        <div>
          <h3 className="font-bold mb-2">Shipping and Returns</h3>
          <ul>
            <li>Copyright</li>
            <li>Terms and conditions</li>
          </ul>
        </div>
      </footer>
    </div>
  );
};

const ServiceTypeSelector = ({ title, options, selected, onSelect }) => (
  <div>
    <h2 className="font-bold mb-2">{title}</h2>
    <div className="flex flex-col space-y-2">
      {options.map((option) => (
        <button
          key={option}
          className={`p-2 border ${selected === option.toLowerCase() ? 'border-black' : 'border-gray-300'}`}
          onClick={() => onSelect(option)}
        >
          {option}
        </button>
      ))}
    </div>
  </div>
);

const PackageSelector = ({ title, services, selected, onSelect }) => (
  <div>
    <h2 className="font-bold mb-2">{title}</h2>
    <div className="flex flex-col space-y-2">
      {services.map((service) => (
        <button
          key={service.title}
          className={`p-2 border ${selected === service ? 'border-black' : 'border-gray-300'}`}
          onClick={() => onSelect(service)}
        >
          {service.title}
        </button>
      ))}
    </div>
  </div>
);

const VehicleTypeSelector = ({ title, types, selected, onSelect }) => (
  <div>
    <h2 className="font-bold mb-2">{title}</h2>
    <div className="flex flex-col space-y-2">
      {types.map((type) => (
        <button
          key={type}
          className={`p-2 border ${selected === type ? 'border-black' : 'border-gray-300'}`}
          onClick={() => onSelect(type)}
        >
          {type}
        </button>
      ))}
    </div>
  </div>
);

const ServiceVisualizer = ({ service }) => (
  <div className="h-64 bg-gray-100 flex items-center justify-center">
    {service ? (
      <img src={`/images/${service.title.toLowerCase().replace(' ', '-')}.jpg`} alt={service.title} className="max-h-full" />
    ) : (
      <p>Select a service to visualize</p>
    )}
  </div>
);

const FeatureList = ({ features }) => (
  <div>
    <h2 className="font-bold mb-2">Features</h2>
    <ul className="list-disc pl-5 space-y-1">
      {features.map((feature, index) => (
        <li key={index}>{feature}</li>
      ))}
    </ul>
  </div>
);

export default ServiceCustomizer;
