// src/components/JRAutoDetailing.js
import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const JRAutoDetailing = () => {
  const [step, setStep] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(1);
    }, 3000); // Transition after 3 seconds
    return () => clearTimeout(timer);
  }, []);

  const handleAnimationComplete = () => {
    setTimeout(() => {
      setStep(2);
    }, 5000); // Stay on the "NEW ORD3R" screen for 5 seconds
  };

  const variants = {
    hidden: { opacity: 0, x: 100 },
    visible: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -100 },
  };

  return (
    <div className="relative w-full h-screen bg-black">
      <AnimatePresence>
        {step === 0 && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 bg-black"
          />
        )}
        {step === 1 && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex flex-col items-center justify-center text-white"
            onAnimationComplete={handleAnimationComplete}
          >
            <div className="text-sm mb-2">LOCATED IN SURREY, B.C.</div>
            <div className="text-6xl font-bold mb-2">JR - Auto - Detailing</div>
            <div className="text-lg mb-2">MODULAR CYCLING SPACE FOR CULTURE</div>
            <div className="text-sm">KARL-MARX-ALLEE 10243 BERLIN</div>
          </motion.div>
        )}
        {step === 2 && (
          <motion.div
            key="services"
            initial="hidden"
            animate="visible"
            exit="exit"
            variants={variants}
            className="absolute inset-0 flex items-center justify-center"
          >
            <div className="text-white text-4xl">Our Services</div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
