import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const services = [
    {
      title: "Interior Silver",
      type: "Interior",
      description: "A thorough cleaning of your vehicle's interior.",
      features: [
        "Vacuum the interior",
        "Power wash & foam floor all mats",
        "Clean and detail the trunk",
        "Detail car doors and all the door grooves",
        "Standard carpet cleaning foam for seats",
        "Leather polisher for leather seats",
        "Use interior polisher on hard surfaces"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Interior Gold",
      type: "Interior",
      description: "An extensive interior detailing service.",
      features: [
        "Deep clean vacuum, the interior",
        "Power washing & foam floor mats",
        "Clean and detail the trunk and use wet vacuum (if needed)",
        "Detail the car door and door grooves",
        "Use interior polisher on hard surfaces",
        "Use my premium upholstery carpet bomber with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "70$",
        "SUV/Pickup Truck": "90$",
        "Large SUV/Lifted Pickup Truck": "110$"
      }
    },
    {
      title: "Interior Platinum",
      type: "Interior",
      description: "Our most comprehensive interior detailing package.",
      features: [
        "Deep clean vacuum, the interior",
        "Car floor will be steamed and agitated to get all micro particles out",
        "Power wash & foam floor mats and use mat polish on them after",
        "Clean and detail the trunk with steamer and wet vacuum",
        "Detail the car doors and all the grooves with lasting polisher",
        "Use interior polisher on all hard surfaces",
        "Use premium upholstery carpet bomber solution with wet vacuum on carpet seats",
        "Use premium, leather polisher on leather seats",
        "All car seats will be steamed",
        "Car will be scented after"
      ],
      prices: {
        "Coupe/Sedan": "90$",
        "SUV/Pickup Truck": "110$",
        "Large SUV/Lifted Pickup Truck": "130$"
      }
    },
    {
      title: "Exterior Silver",
      type: "Exterior",
      description: "A comprehensive cleaning of your vehicle's exterior.",
      features: [
        "Wheel and Tire Cleaning",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Outside door jams",
        "Exterior Window Cleaning"
      ],
      prices: {
        "Coupe/Sedan": "30$",
        "SUV/Pickup Truck": "50$",
        "Large SUV/Lifted Pickup Truck": "70$"
      }
    },
    {
      title: "Exterior Gold",
      type: "Exterior",
      description: "An advanced exterior detailing service.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Clay mitt (Removes Embedded Contaminants)",
        "Snow Foam (Hand Wash)",
        "Premium exterior polisher",
        "Door Jambs Cleaned"
      ],
      prices: {
        "Coupe/Sedan": "50$",
        "SUV/Pickup Truck": "70$",
        "Large SUV/Lifted Pickup Truck": "90$"
      }
    },
    {
      title: "Exterior Platinum",
      type: "Exterior",
      description: "Our most comprehensive exterior detailing package.",
      features: [
        "Wheel and Tire Cleaning",
        "Tires Shined",
        "Pre Wash (Removes Dirt and Grime, No Contact)",
        "Snow Foam (Hand Wash)",
        "Door Jambs Cleaned",
        "Exterior Window Cleaning",
        "Clay Mitt & Clay bar (Removes Rough Embedded Contaminants)",
        "Whole car will be buffed (will take out scratches and micro scratches)"
      ],
      prices: {
        "Coupe/Sedan": "80$",
        "SUV/Pickup Truck": "100$",
        "Large SUV/Lifted Pickup Truck": "120$"
      }
    }
  ];

  const ServiceSection = ({ services }) => {
    const [filters, setFilters] = useState({
      carType: 'all',
      tier: 'all',
      serviceType: 'all'
    });
  
    const [filteredServices, setFilteredServices] = useState(services);
  
    useEffect(() => {
      const newFilteredServices = services.filter(service => 
        (filters.carType === 'all' || service.prices[filters.carType]) &&
        (filters.tier === 'all' || service.title.toLowerCase().includes(filters.tier)) &&
        (filters.serviceType === 'all' || service.type === filters.serviceType)
      );
      setFilteredServices(newFilteredServices);
    }, [filters, services]);
  
    const containerVariants = {
      hidden: { y: "100%" },
      visible: { 
        y: 0,
        transition: { 
          type: "spring",
          stiffness: 50,
          when: "beforeChildren",
          staggerChildren: 0.1
        }
      }
    };
  
    const itemVariants = {
      hidden: { opacity: 0, y: 20 },
      visible: { opacity: 1, y: 0 }
    };
  
    return (
      <motion.div 
        className="bg-black text-white p-8"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        <h1 className="text-6xl font-bold mb-8">SERVICES</h1>
        
        <div className="mb-8">
          <select 
            onChange={(e) => setFilters({...filters, carType: e.target.value})}
            className="bg-white text-black p-2 mr-4"
          >
            <option value="all">All Car Types</option>
            <option value="Coupe/Sedan">Coupe/Sedan</option>
            <option value="SUV/Pickup Truck">SUV/Pickup Truck</option>
            <option value="Large SUV/Lifted Pickup Truck">Large SUV/Lifted Pickup Truck</option>
          </select>
  
          <select 
            onChange={(e) => setFilters({...filters, tier: e.target.value})}
            className="bg-white text-black p-2 mr-4"
          >
            <option value="all">All Tiers</option>
            <option value="silver">Silver</option>
            <option value="gold">Gold</option>
            <option value="platinum">Platinum</option>
          </select>
  
          <select 
            onChange={(e) => setFilters({...filters, serviceType: e.target.value})}
            className="bg-white text-black p-2"
          >
            <option value="all">All Service Types</option>
            <option value="Interior">Interior</option>
            <option value="Exterior">Exterior</option>
          </select>
        </div>
  
        <AnimatePresence>
          {filteredServices.map((service, index) => (
            <motion.div 
              key={service.title}
              variants={itemVariants}
              initial="hidden"
              animate="visible"
              exit="hidden"
              className="mb-8"
            >
              <h2 className="text-4xl font-bold mb-2">{service.title.toUpperCase()}</h2>
              <p className="mb-2">{service.description}</p>
              <ul className="list-disc pl-5 mb-2">
                {service.features.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
              <div>
                {Object.entries(service.prices).map(([carType, price]) => (
                  <p key={carType}>{carType}: {price}</p>
                ))}
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
      </motion.div>
    );
  };
  
  export default ServiceSection;
