// src/components/JRAutoDetailing.js
import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const JRAutoDetailing = () => {
  const [showImage, setShowImage] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowImage(true);
    }, 3000); // Transition after 3 seconds
    return () => clearTimeout(timer);
  }, []);

  const variants = {
    hidden: { opacity: 0, x: 100 },
    visible: { opacity: 1, x: 0 },
  };

  return (
    <div className="relative w-full h-screen bg-gray-900">
      <AnimatePresence>
        {!showImage && (
          <motion.div
            key="background"
            initial="hidden"
            animate="visible"
            exit="hidden"
            variants={variants}
            className="absolute inset-0 bg-gray-900"
          />
        )}
        {showImage && (
          <motion.div
            key="image"
            initial="hidden"
            animate="visible"
            exit="hidden"
            variants={variants}
            className="absolute inset-0 flex items-center justify-center"
          >
            <img src="/path/to/your/image.jpg" alt="JR Auto Detailing" className="w-full h-full object-cover" />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JRAutoDetailing;
